INSERT INTO `articles` VALUES ('155', '广西医科大学2016年成人高等教育招生简章', '', '<p style=\"font-family: Simsun;font-size:16px; text-align: center; line-height: 1.5em; margin-left: 0px; margin-right: 0px;\"><strong>广西医科大学</strong></p><p style=\"font-family: Simsun;font-size:16px; line-height: 1.5em; margin-left: 0px; margin-right: 0px;\"><strong>&nbsp;学校简介</strong></p><p style=\"font-family: Simsun;font-size:16px; line-height: 1.5em; margin-left: 0px; margin-right: 0px;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-family: 宋体; font-size: 14px;\">广西医科大学人教育学院是在1991年11月15日，根据区教委广西壮族自治区桂教函人字[1991]248号批文而成立。从一个业余、脱产学生二千多人发展到现今的一万多学生；从一、二个专科专业，发展到现在的本专科九个专业，为社会和学校产生了较大的社会效益和经济效益。</span><span style=\"font-family: 宋体; font-size: 14px;\"><br /></span><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-family: 宋体; font-size: 14px;\">继续教育学院是为了适应广西医疗卫生事业发展及医技人才的培养迫切需求，根据卫生部对各高等医科院校的要求，以及学校校人字[2001]03号《关于成立广西医科大学继续教育学院的通知》的文件精神，于二00一年二月二十四日宣告成立。成立后的一年中，继教院积极组织校内各二级学院的继续教育工作与继教项目申报及执行，进一步完善进修学习制度，并接受卫生厅有关部门委托开办全科医师岗位培训班及卫生系列职称考前培训工作，该院将在新的一年里，加强学习，强化服务，为广西卫生事业在职在岗人员的再教育作出应有的努力。</span></p><p style=\"font-family: Simsun;font-size:16px; line-height: 1.5em; margin-left: 0px; margin-right: 0px;\"><strong>特色专业：</strong><span style=\"font-family: 宋体; font-size: 14px;\">护理、临床医学、药学、中西医结合、护理学</span></p><p style=\"font-family: Simsun;font-size:16px; line-height: 1.5em;\"><strong>招生专业：</strong></p><p style=\"font-family: Simsun;font-size:16px; line-height: 1.5em; margin-left: 0px; margin-right: 0px;\"><strong></strong></p><p style=\"font-family: Simsun;font-size:16px; line-height: 1.5em; margin-left: 0px; margin-right: 0px;\"><strong></strong></p><p style=\"font-family: Simsun;font-size:16px; line-height: 1.5em; margin-left: 0px; margin-right: 0px;\"><strong></strong></p><p style=\"font-family: Simsun;font-size:16px; line-height: 1.5em; margin-left: 0px; margin-right: 0px;\"><strong></strong></p><p sty