-- ----------------------------
--  Table structure for `articles`
-- ----------------------------
DROP TABLE IF EXISTS `articles`;
CREATE TABLE `articles` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(255) NOT NULL DEFAULT '',
  `video` varchar(1000) NOT NULL DEFAULT '',
  `content` text,
  `keywords` varchar(255) NOT NULL DEFAULT '',
  `description` varchar(1000) NOT NULL DEFAULT '',
  `create_time` int(10) unsigned NOT NULL DEFAULT '0',
  `visit_count` int(10) unsigned NOT NULL DEFAULT '0',
  `user_id` int(10) unsigned NOT NULL DEFAULT '0',
  `author` varchar(60) NOT NULL DEFAULT '',
  `from_url` varchar(255) NOT NULL DEFAULT '',
  `thumb` varchar(255) NOT NULL DEFAULT '',
  `cate_id` smallint(5) unsigned NOT NULL DEFAULT '0',
  `source` varchar(60) NOT NULL DEFAULT '',
  `status` tinyint(1) NOT NULL DEFAULT '1',
  PRIMARY KEY (`id`),
  KEY `cate_id` (`cate_id`) USING BTREE,
  KEY `user_id` (`user_id`) USING BTREE,
  KEY `status` (`status`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=76 DEFAULT CHARSET=utf8;

-- ----------------------------
--  Records of `articles`
-- ----------------------------
BEGIN;
INSERT INTO `articles` VALUES ('1', '十二种夏日降火的食物', '', '<p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">夏天艳阳似火，很多人会出现口干舌燥、溃疡频发、小便赤黄、大便干结，甚至心慌胸闷、脾气暴躁、夜睡不佳等现象。究其原因，是因为天气炎热、肝火旺盛所致。那么夏季去火吃什么好呢?</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　1、海带</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　海带既可凉拌，又可煲汤食用，其中</span><tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"牛肉\" style=\"display: inline;\"><span style=\"color:#666666;margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background-color: transparent; text-decoration: none; display: inline;\">牛肉</span></tuihighlight><span style=\"color:#666666;\">海带汤特别适合夏季饮用。　　海带不仅有助于清除体内的积热，还可以对付因出汗过多出现的体力透支。另外，夏季是心脑血管疾病的高发季节，海带中含有的多种营养物质恰恰具有辅助降低血压、降低血液黏稠度的作用，对心脑血管疾病患者大有裨益。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　禁忌：但患有甲亢的病人不要吃海带，</span><tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"孕妇\" style=\"display: inline;\"><span style=\"color:#666666;\">孕妇</span></tuihighlight><span style=\"color:#666666;\">、乳母也不宜吃过多海带。</span></p><p style=\"text-align: justify; margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img src=\"/data/upload/2d/1i/51fbde9f667f4_600x480.jpg\" width=\"350\" alt=\"\" /><br /></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　2、黄瓜</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　黄瓜夏季吃不仅爽口、清热、解暑，还能增进食欲。因为其中所含的葡萄糖甙、果糖等不参与通常的糖代谢，糖尿病人食用黄瓜，血糖非但不会升高，甚至会降低。</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　禁忌：需要提醒的是，由脾胃虚寒导致的胃痛、慢性腹泻患者最好少吃黄瓜，老年慢性支气管炎发作期也最好不要食用。</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　3、西红柿</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　西红柿一年四季都可见，营养也很丰富，它同样也是清热解毒的好帮手，用中医的话来说，西红柿的作用就是平肝“去火”。</p><p style=\"text-align: justify; margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img src=\"/data/upload/7s/2z/51fbde3601382_600x480.jpg\" width=\"350\" alt=\"\" /><br /></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　民间把萝卜称为“小</span><tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"人参\" style=\"display: inline;\"><span style=\"color:#666666;margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background-color: transparent; text-decoration: none; display: inline;\">人参</span></tuihighlight><span style=\"color:#666666;\">”，这可不是完全没有道理的哦。萝卜不仅营养价值高，还是预防上火的好手。对已经气郁上火甚至生痰的人来说，萝卜有很好的清热消痰作用。如果要比拼“降火”能力的话，青萝卜是最佳，红皮白心者次之。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　5、大豆</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　大豆蕴含丰富的蛋白质，在滋阴去火的同时还能为你补充蛋白质、异黄酮、低聚糖、皂苷、磷脂、核酸等营养。除此之外，大豆中含有的可溶性纤维在帮你降低胆固醇含量的同时，还可以帮你顺利通便排毒，你的皮肤和气色也会因此好很多哦!</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　6、<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"蜂蜜\" style=\"display: inline;\">蜂蜜</tuihighlight></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　甜甜的</span><tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"蜂蜜\" style=\"color: rgb(102, 102, 102); display: inline;\">蜂蜜</tuihighlight><span style=\"color:#666666;\">也是超棒的去火食物!《</span><tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"本草纲目\" style=\"display: inline;\"><span style=\"color:#666666;margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background-color: transparent; text-decoration: none; display: inline;\">本草纲目</span></tuihighlight><span style=\"color:#666666;\">》上记载，</span><tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"蜂蜜\" style=\"display: inline;\"><span style=\"color:#666666;\">蜂蜜</span></tuihighlight><span style=\"color:#666666;\">有“清热、解毒、润燥”等功效，是“健康长寿的妙药”。早上起床后喝一大杯温</span><tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"蜂蜜\" style=\"display: inline;\"><span style=\"color:#666666;\">蜂蜜</span></tuihighlight><span style=\"color:#666666;\">水，除了能帮你化解“夏热”之外，还能给你带来一天的甜蜜好心情呢!除此之外，如果你还因上火而导致嘴唇干燥或龟裂，也可以通过</span><tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"蜂蜜\" style=\"color: rgb(102, 102, 102); display: inline;\">蜂蜜</tuihighlight><span style=\"color:#666666;\">来补救哦!你可以在就寝前，把</span><tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"蜂蜜\" style=\"color: rgb(102, 102, 102); display: inline;\">蜂蜜</tuihighlight><span style=\"color:#666666;\">细心地涂抹在嘴唇上，渗透一会儿后再涂上护唇膏。几天后，嘴唇就可恢复柔嫩光滑。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"></p><p style=\"color: rgb(102, 102, 102); margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\">&nbsp; &nbsp;7、<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"牛奶\" style=\"display: inline;\">牛奶</tuihighlight></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"牛奶\" style=\"display: inline;\"><span style=\"color:#666666;margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background-color: transparent; text-decoration: none; display: inline;\">&nbsp; &nbsp; &nbsp; 牛奶</span></tuihighlight><span style=\"color:#666666;\">不仅营养丰富，而且还是优质的“灭火”剂呢!中医认为</span><tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"牛奶\" style=\"color: rgb(102, 102, 102); display: inline;\">牛奶</tuihighlight><span style=\"color:#666666;\">性微寒，可以通过滋阴、解热毒来发挥“去火”功效，而且</span><tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"牛奶\" style=\"display: inline;\"><span style=\"color:#666666;\">牛奶</span></tuihighlight><span style=\"color:#666666;\">中含有多达70%左右的水分，还能补充人体因大量出汗而损失的水分。</span></p><p style=\"text-align: justify; margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img src=\"/data/upload/2y/1j/51fbdddf6c63b_600x480.jpg\" width=\"350\" alt=\"\" /><br /></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">&nbsp; &nbsp;8、梨　　<br /></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　“夏热”的起因是春天里的空气干燥、湿度低，但是如果你身上出现了上火的迹象，那就说明你的</span><tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"新鲜水果\" style=\"display: inline;\"><span style=\"color:#666666;margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background-color: transparent; text-decoration: none; display: inline;\">新鲜水果</span></tuihighlight><span style=\"color:#666666;\">和蔬菜摄入量还是不足哦!梨子是众所周知的降火水果，多吃梨对扑灭你的“火气”可是很有帮助的哦!</span></p><p style=\"color: rgb(102, 102, 102); margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\">　9、西瓜 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p><p style=\"color: rgb(102, 102, 102); margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\">　　　　西瓜为夏令的主要瓜果，所以又称为“夏瓜”。西瓜一身都是宝：西瓜瓤、西瓜水可以补充人体的皮肤、肌肉、毛发在炎热夏季的营养需要。尤其是西瓜所含的维生素、酶类、氨基酸等营养物质，能够滋养皮肤，促进食欲，是夏令食疗佳品。</p><p style=\"text-align: left; color: rgb(102, 102, 102); margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img src=\"/data/upload/2q/4t/51fbdd812ff01_600x480.jpg\" width=\"350\" alt=\"\" /><br /></p><p style=\"color: rgb(102, 102, 102); margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\">&nbsp; &nbsp; &nbsp; &nbsp;10、蛤蜊<br /></p><p style=\"color: rgb(102, 102, 102); margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\">　　蛤蜊具有高蛋白、低脂肪的特点，有助于抑制胆固醇在肝脏合成、加速胆固醇排泄。夏季的蛤蜊肥厚，吃起来口感更好。</p><p style=\"color: rgb(102, 102, 102); margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\">　　蛤蜊的吃法很多，这个季节最好选择清水煮蛤蜊和蛤蜊豆腐汤。需要特别提醒的是，无论是清水煮还是做汤，蛤蜊都要凉水下锅，在预热的过程中，蛤蜊就会慢慢的张开口，这时肉也熟了。如果热水下锅的话，刚一下锅，蛤蜊就会马上张口，等煮熟了，肉就变老了，失去鲜嫩的口感了。</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　禁忌：女性月经期间及产后忌食蛤蜊，受凉感冒者也忌食。另外，蛤蜊不宜与</span><tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"啤酒\" style=\"display: inline;\"><a href=\"http://www.meishij.net/changshi/pandianxiariquhuode12zhongshiwu-2.html#\" data-keyword=\"啤酒\" data-type=\"2\" data-style=\"1\" data-tmpl=\"290x380\" data-tmplid=\"53\" target=\"_blank\" style=\"margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background-color: transparent; text-decoration: none; display: inline;\"><span style=\"color:#666666;\">啤酒</span></a></tuihighlight><span style=\"color:#666666;\">同食，否则容易诱发痛风。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　11、</span><tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"莲子\" style=\"display: inline;\"><span style=\"color:#666666;\">莲子</span></tuihighlight></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　夏季最适宜养心，因为夏日心脏最脆弱，暑热逼人容易烦躁伤心，易伤心血。</span><tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"莲子\" style=\"display: inline;\"><span style=\"color:#666666;margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background-color: transparent; text-decoration: none; display: inline;\">莲子</span></tuihighlight><span style=\"color:#666666;\">芯的味道虽然比较苦，但可以清心火，是养心安神的佳品，亦可壮肠胃。</span></p><p style=\"color: rgb(102, 102, 102); margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\">　　12、绿豆</p><p style=\"color: rgb(102, 102, 102); margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\">　　夏天在高温环境工作的人出汗多，水液损失很大，体内的电解质平衡遭到破坏，用绿豆煮汤来补充是最理想的方法，能够清暑益气、止渴利尿，不仅能补充水分，而且还能及时补充无机盐，对维持水液电解质平衡有着重要意义。</p><br /><br /><p><br /></p><p><br /></p><p><br /></p><p><br /></p>', '', '', '1375281109', '199', '1', '', '', '', '3', '周边小编', '1'), ('2', '南宁外卖-夏天5大超级食物别错过', '', '<p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">这个富含夏季阳光的水果，不仅为您提供日常必需的维生素，它还含有纤维、镁和抗氧化剂。</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative; text-align: center;\"><img class=\"conimg\" height=\"300\" alt=\"正文.jpg\" width=\"350\" src=\"/data/upload/5g/2h/51fbe1580a0ab_600x480.jpg\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　1、<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"芒果\" style=\"display: inline;\">芒果</tuihighlight></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　爱吃甜食的MM们注意了，如果很想吃甜食的话，来一杯<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"芒果\" style=\"display: inline;\">芒果</tuihighlight>汁或者是新鲜<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"芒果\" style=\"display: inline;\">芒果</tuihighlight>，加点<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"酸奶\" style=\"display: inline;\"><span style=\"margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background-color: transparent; text-decoration: none; display: inline;\">酸奶</span></tuihighlight>，会是比任何甜品都要健康的天然甜品呢!&nbsp;<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"芒果\" style=\"display: inline;\">芒果</tuihighlight>富含铁，能够有效改善缺铁症状，<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"芒果\" style=\"display: inline;\">芒果</tuihighlight>的另一个好处是帮助消化。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　2、无花果</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color: rgb(102, 102, 102);\">　　无花果富含维生素和矿物质，包括维生素B 1、B 2、钙、铁、磷、锰和钾。除了营养全面，还可以帮助减肥。原因</span><span style=\"color:#666666;\">是它富含<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"膳食纤维\" style=\"display: inline;\">膳食纤维</tuihighlight>，能够对减肥产生积极影响。纤维有助于消除脂肪和毒素，将其在体内的冲洗出来;还能改善代谢和消化，防止脂肪困在无用的地方。每次食用三到五个无花果干即可，或者把无花果干放入<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"麦片\" style=\"display: inline;\">麦片</tuihighlight>中，也是不错的选择。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative; text-align: center;\"><img class=\"conimg\" height=\"300\" alt=\"正文2.jpg\" width=\"350\" src=\"/data/upload/4c/4i/51fbe2d416bcd_600x480.jpg\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　3、李子</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　夏季集中上市的水果之一，一个中等大小的李子仅仅有30卡路里，富含维生素A、B、C、钾以及纤维素。这些营养物质可以帮助保护细胞，保持心脏健康，增强免疫力。在家可以直接生吃李子或者做成水果拼盘，尽量不要多加工!</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　4、油桃</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　这些夏季水果含有矿物质硼，有助于体内激素稳定，能够降低经前综合症和更年期的对情绪的影响。另外油桃含β-胡萝卜素，维生素B 1、B 2、B 3，铁和钙。油桃可以制成点心来食用，能够有效缓解你对甜食的渴望哦!</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　5、豆薯</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　豆薯含有钾、钙和铁，富含维生素、鞣花酸和纤维。鞣花酸帮助人体分解糖分，从而降低血糖水平。豆薯富含鞣花酸和维生素，这有助于身体利用葡萄糖作为能量，而不是将其储存为脂肪。此外，豆薯可以有效改善腹胀的状况，用它制成的松脆的点心是不可比拟的点心好物哦!</p>', '', '', '1375461581', '199', '1', '', '', '', '3', '周边小编', '1'), ('3', '夏季最佳食物防暑排毒', '', '<p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">夏季，天气很炎热，出门在外尤其是老人小孩一定要做好防暑降温的工作准备。在这炎热的天气里吃什么更利于健康呢?现在小编带大家一起来了解下炎夏我们要如何养生。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative; text-align: center;\"><img class=\"conimg\" height=\"300\" alt=\"正文.jpg\" width=\"350\" src=\"/data/upload/7i/6c/51fbe453ae9ca_600x480.jpg\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　1、苦瓜</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　苦瓜又名“凉瓜”，是夏季消暑的最好<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"蔬菜\" style=\"display: inline;\">蔬菜</tuihighlight>，营养非常丰富，有清热解暑、健脾开胃、降血糖、美容、防癌等功效，大暑天热，吃苦瓜最好。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　2、鸭肉</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　鸭肉性寒，是夏季进补的优良<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"食品\" style=\"display: inline;\">食品</tuihighlight>，营养很高，有除湿、解毒、滋阴、去燥、养胃、补肾、消水肿、止咳化痰等功效，非常适合炎热天气的时候吃。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　3、西瓜</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　西瓜民间又叫“寒瓜”，味甘甜、性寒，是瓜类中消暑解渴的首选。民间有“每天半个瓜，酷暑能算啥”的说法。夏天出现中暑、发热、心烦、口渴或其他急性热病时，均宜用西瓜进行辅助治疗。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative; text-align: center;\"><img class=\"conimg\" height=\"300\" alt=\"正文2.jpg\" width=\"350\" src=\"/data/upload/1p/6r/51fbe44633253_600x480.jpg\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　4、绿豆</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　绿豆是夏季饮食中的上品，营养丰富，有清热消暑、美容养颜、止渴利尿等功效，有“济世之食谷”之美誉。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　5、西红柿</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　西红柿是大家最为熟悉的果蔬之一，性凉，营养丰富、酸甜爽口，有清热止渴，养阴凉血、生津止渴、美容、防辐射、减肥等功效。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　西红柿防中暑：将1～2个西红柿切片，加盐或糖少许，熬汤热饮，可防中暑。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　6、<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"绿茶\" style=\"display: inline;\">绿茶</tuihighlight></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"绿茶\" style=\"display: inline;\">绿茶</tuihighlight>属未发酵茶，性寒，“寒可清热”，最能去火，有提神清心、清热解暑、消食化痰、去腻减肥、清心除烦、解毒醒酒、生津止渴、降火明目等功效，最适合夏天喝。</span></p>', '', '', '1375462491', '220', '1', '', '', '', '3', '周边小编', '1'), ('4', '吃8类食物塑造健康乳房', '', '<p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">想让自己的乳房变得健康美丽，就要从日常饮食做起。比如大豆、海带、各种水果和谷类，记住健康的身体绝不能挑食，因为饮食均衡才能保证你的身体不缺少各种营养。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative; text-align: center;\"><img class=\"conimg\" height=\"300\" alt=\"正文.jpg\" width=\"350\" src=\"/data/upload/2t/5z/51fbe63b696bd_600x480.jpg\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　增加大豆<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"食品\" style=\"display: inline;\">食品</tuihighlight>对乳房健康大有裨益。因为，大豆和由大豆加工而成的<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"食品\" style=\"display: inline;\">食品</tuihighlight>中含有异黄酮，这种物质能够降低女性体内的雌激素水平，减少乳房不适。如果每天吃两餐含有大豆的<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"食品\" style=\"display: inline;\">食品</tuihighlight>，比如豆腐、豆浆等，将会对乳房健康十分有益。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　银耳、黑木耳、香菇、猴头菇、茯苓等食物，是天然的生物反应调节剂，能增强人体免疫能力，有较强的防癌作用。研究表明，多吃食用菌可为女性的乳房健康加分。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　海带是一种大型食用藻类，对于女性来说，不仅有美容、美发、瘦身等保健作用，还能辅助治疗乳腺增生。研究发现，海带之所以具有缓解乳腺增生的作用，是由于其中含有大量的碘，可促使卵巢滤泡黄体化，使内分泌失调得到调整，降低女性患乳腺增生的风险。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative; text-align: center;\"><img class=\"conimg\" height=\"300\" alt=\"正文2.jpg\" width=\"350\" src=\"/data/upload/8b/8h/51fbe62fe437b_600x480.jpg\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　种子、坚果类食物包括含卵磷脂的黄豆、花生等，含丰富蛋白质的<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"杏仁\" style=\"display: inline;\">杏仁</tuihighlight>、<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"核桃\" style=\"display: inline;\">核桃</tuihighlight>、芝麻等，其中含有大量的抗氧化剂，可起到抗癌的效果。而且，坚果和种子<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"食品\" style=\"display: inline;\">食品</tuihighlight>可增加人体对<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"维生素e\" style=\"display: inline;\">维生素E</tuihighlight>的摄入，而摄入丰富的<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"维生素e\" style=\"display: inline;\">维生素E</tuihighlight>能让乳房组织更富有弹性。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　黄鱼、甲鱼、泥鳅、带鱼、章鱼、鱿鱼、海参、牡蛎以及海带、海蒿子等，富含人体必需的微量元素，有独特的保护乳腺的作用。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　葡萄、猕猴桃、柠檬、草莓、柑橘、无花果等，在让女性摄取多种维生素的同时，也获得抗乳腺癌的物质。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　蔬菜与主食合理搭配，不仅有利于身体健康，如果每天的饮食保证摄取足够的蔬菜，多食番茄、胡萝卜、菜花、南瓜、大蒜、洋葱、芦笋、黄瓜、丝瓜、萝卜和一些绿叶蔬菜等，对维护乳房的健康很有帮助。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　谷类如小麦(面粉)、玉米及一些杂粮经常食用，均对乳房具有保健作用。小麦含有大量的可溶性和不可溶性纤维素，可溶性纤维素帮助身体降低胆固醇;不可溶性纤维素有助于预防癌症，玉米更被营养专家肯定为最佳的丰胸<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"食品\" style=\"display: inline;\">食品</tuihighlight>。</span></p>', '', '', '1375462978', '395', '1', '', '', '', '3', '周边小编', '1'), ('5', '揭开苹果鲜为人知秘密', '', '<p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">众所周知，为了身体健康，应当每天吃一个<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"苹果\" style=\"display: inline;\">苹果</tuihighlight>。<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"苹果\" style=\"display: inline;\">苹果</tuihighlight>是最受欢迎的水果之一，人们可以很容易就食用到这种富含膳食纤维、口感酥脆多汁的水果。富含<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"苹果\" style=\"display: inline;\">苹果</tuihighlight>的饮食方案能让人获得一系列健康功效，包括减轻体重、增强肺功能，降低中风、癌症和心脏病的发病率。就算<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"苹果\" style=\"display: inline;\">苹果</tuihighlight>是一种常见水果，它也有其神秘之处。美国《赫芬顿邮报》对<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"苹果\" style=\"display: inline;\">苹果</tuihighlight>中隐藏的五个秘密进行了介绍。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative; text-align: center;\"><img class=\"conimg\" height=\"300\" alt=\"正文.jpg\" width=\"350\" src=\"/data/upload/1n/5c/51fbe7620a753_600x480.jpg\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　一、对<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"苹果\" style=\"display: inline;\">苹果</tuihighlight>过敏属于花粉过敏症</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　一提到食物过敏，人们通常会想到花生和甲壳类动物，甚至是<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"鸡蛋\" style=\"display: inline;\">鸡蛋</tuihighlight>。事实上，对<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"苹果\" style=\"display: inline;\">苹果</tuihighlight>的轻微过敏症并不罕见。专家认为在未完全成熟的<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"苹果\" style=\"display: inline;\">苹果</tuihighlight>表面通常存在着桦树的花粉，这些花粉是导致过敏反应的原因，而并非是<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"苹果\" style=\"display: inline;\">苹果</tuihighlight>肉内包含的某种化合物。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　二、<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"苹果\" style=\"display: inline;\">苹果</tuihighlight>是一种真正的<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"减肥食品\" style=\"display: inline;\">减肥食品</tuihighlight></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"苹果\" style=\"display: inline;\">苹果</tuihighlight>中富含水分和膳食纤维，尽管它所含有的热量很少(一个<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"苹果\" style=\"display: inline;\">苹果</tuihighlight>的热量约为95千卡)，但它却能让人的饱腹感延长。<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"苹果\" style=\"display: inline;\">苹果</tuihighlight>还有燃烧脂肪的额外优势：<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"苹果\" style=\"display: inline;\">苹果</tuihighlight>皮中一种名为熊果酸的化合物有助于减少老鼠体内的棕色脂肪，而且熊果酸还能加快热量燃烧，降低老鼠患上肥胖症的风险。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　三、<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"苹果\" style=\"display: inline;\">苹果</tuihighlight>的种类非常丰富</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"苹果\" style=\"display: inline;\">苹果</tuihighlight>是世界上种类最丰富的食用植物。据<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"食品\" style=\"display: inline;\">食品</tuihighlight>记者与<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"苹果\" style=\"display: inline;\">苹果</tuihighlight>专家罗恩·雅各布森声称，仅美国过去就存在1.6万种<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"苹果\" style=\"display: inline;\">苹果</tuihighlight>。美国农业部目前记录在案的<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"苹果\" style=\"display: inline;\">苹果</tuihighlight>就有2450种。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　四、通常的吃<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"苹果\" style=\"display: inline;\">苹果</tuihighlight>方法会浪费三分之一的果肉</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　沿着<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"苹果\" style=\"display: inline;\">苹果</tuihighlight>核呈环状啃食<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"苹果\" style=\"display: inline;\">苹果</tuihighlight>会浪费30%左右的果肉(热量在20千卡-30千卡之间)。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　五、<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"苹果\" style=\"display: inline;\">苹果</tuihighlight>中包含很多空气</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　网络上流传一种未经证实的说法，认为<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"苹果\" style=\"display: inline;\">苹果</tuihighlight>包含25%的空气;但唯一找到的有关研究数据显示它的空气含量接近18%左右。</span></p>', '', '', '1375463275', '188', '1', '', '', '', '3', '周边小编', '1'), ('6', '13种让人变聪明的食物', '', '<p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">药补不如食补，吃对了还能促进大脑健康。营养学家推荐了13种能让人变聪明的食物，快跟随小编一起来看看吧。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative; text-align: center;\"><img class=\"conimg\" height=\"300\" alt=\"正文.jpg\" width=\"350\" src=\"/data/upload/2o/2c/51fbe9efcd7cb_600x480.jpg\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　1、胡萝卜</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　β胡萝卜素和其他天然营养物有助于保护大脑。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　2、西蓝花</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　丰富的抗氧化剂和<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"植物\" style=\"display: inline;\">植物</tuihighlight>营养素，有助于保护大脑组织免受毒素侵害。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　3、甜菜、毛豆</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　富含氨基酸苯丙氨酸，有助增强脑细胞活力。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　4、瘦牛排、菠菜</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　富含矿物质铁，有助于提高警觉度和注意力。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative; text-align: center;\"><img class=\"conimg\" height=\"300\" alt=\"正文2.jpg\" width=\"350\" src=\"/data/upload/9r/5d/51fbe9fe0b3cc_600x480.jpg\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　5、鸡肉</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　富含酪氨酸，促进多巴胺、肾上腺素和去甲肾上腺素的分泌。这些物质可以使思维更敏捷，反应更迅速，精力更充沛。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　6、柑橘</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　含大量维生素C，有助于保持记忆力，防止脑细胞受损。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　7、<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"蓝莓\" style=\"display: inline;\">蓝莓</tuihighlight></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　花色苷可促进大脑对葡萄糖的利用。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　8、<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"鸡蛋\" style=\"display: inline;\">鸡蛋</tuihighlight></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　其中的B族维生素胆碱，能帮助改善记忆力。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　9、酸奶</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　多项研究发现，酸奶等含益生菌的食物可增强大脑警觉度。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative; text-align: center;\"><img class=\"conimg\" height=\"300\" alt=\"正文3.jpg\" width=\"350\" src=\"/data/upload/7u/8n/51fbea0b236b9_600x480.jpg\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　10、扁豆</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　能刺激循环，帮助消化，直接向大脑传递快乐感觉。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　11、金枪鱼</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　富含欧米伽-3脂肪酸，有助于促进脑细胞生长。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　12、猪肉</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　含维生素B1，可保护脑细胞所必需的磷脂。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　13、豌豆荚</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　可为大脑提供能量丰富的纤维素，降低葡萄糖的吸收速度。</span></p>', '', '', '1375463957', '222', '1', '', '', '', '3', '周边小编', '1'), ('7', '西瓜的健康功效谁知道', '', '<p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">在夏季水果中，西瓜是很多人的最爱。但是你真的了解西瓜吗?近日，日本《女子》<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"杂志\" style=\"display: inline;\">杂志</tuihighlight>报道，日本营养师总结了有关西瓜你不知道的几件事。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative; text-align: center;\"><img class=\"conimg\" height=\"300\" alt=\"正文.jpg\" width=\"350\" src=\"/data/upload/9v/7b/51fbeb2763804_600x480.jpg\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　一、富含三种护肤成分，能防晒抗老化</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　众所周知，番茄红素是西红柿含有的一种强效抗氧化成分，它可有效防止自由基、紫外线及各种外部辐射对皮肤细胞的损害。但很少有人知道，西瓜中番茄红素含量是西红柿的1.4倍之多。此外，西瓜还富含β胡萝卜素，它是胡萝卜和菠菜等黄绿色蔬菜富含的一种营养素，能在人体内转化成维生素A，有对抗皮肤老化和防癌效果，还能保护和提高视力。值得一提的是，西瓜中还含瓜氨酸，它是西瓜特有的一种氨基酸，具有恢复血管弹性的作用。瓜氨酸还有抗氧化和抑制<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"胶原蛋白\" style=\"display: inline;\">胶原蛋白</tuihighlight>分解的作用，能对皮肤起到保湿、抵御紫外线伤害的效果。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　二、红瓤西瓜比黄瓤的营养好</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　黄瓤西瓜几乎不含番茄红素，因此买西瓜时首选红瓤西瓜。需要注意的是，在挑红瓤西瓜时，要选黑籽的，红瓤白籽有可能是催熟的。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　三、搭配油或全脂乳制品一起吃</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　番茄红素和β胡萝卜素都是脂溶性维生素，因此单独吃西瓜会影响它们的吸收，建议把西瓜切成小块和全脂<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"酸奶\" style=\"display: inline;\">酸奶</tuihighlight>或沙拉混合后食用。吃西瓜时喝袋<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"牛奶\" style=\"display: inline;\">牛奶</tuihighlight>也能起到上述作用。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　四、西瓜皮能美容</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　西瓜皮比肉中的瓜氨酸含量要高约2倍，因此也具有一定的美容护肤功效。吃时可以先削去西瓜的“翠衣”(青皮层)，洗净切条后炒着吃，做法跟炒黄瓜类似。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　五、西瓜籽有助降血压</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　西瓜籽富含不饱和脂肪酸，有降血压、预防动脉硬化的作用。整粒吃不方便，可将其打成粉冲服，或直接跟西瓜一起榨汁喝。</span></p>', '', '', '1375464235', '196', '1', '', '', '', '3', '周边小编', '1'), ('8', '周边告诉你，健康的“粗茶淡饭”是什么', '', '<p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">俗话说“粗茶淡饭最养生”，你知道粗茶淡饭怎么理解吗?中医认为，粗茶是指较粗老的<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"茶叶\" style=\"display: inline;\">茶叶</tuihighlight>，含有的茶多酚、茶丹宁等物质对身体有益。淡饭包含两层意思：一是淡而不咸;二是淡而不腻，既包含谷类食物和蔬菜，也包括脂肪含量低的鸡肉、鸭肉、鱼肉、牛肉等。粗茶淡饭，即日常饮食以蔬菜等植物性食物为主，注意粮豆混食、米面混食，适当辅以包括肉类在内的各种动物性<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"食品\" style=\"display: inline;\">食品</tuihighlight>，常喝粗茶。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　最好的补药是营养丰富的食物，食补胜过药补，也就是人们常说“粗茶淡饭最养生”，因为许多蔬菜和粮食，本身亦可称作药物，所以摄取<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"五谷杂粮\" style=\"display: inline;\">五谷杂粮</tuihighlight>、根茎叶菜便可起到保健养生的的作用。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative; text-align: center;\"><img class=\"conimg\" height=\"300\" alt=\"正文.jpg\" width=\"350\" src=\"/data/upload/9c/6p/51fbed000ee6d_600x480.jpg\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　一、解读粗茶</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　粗茶是指较粗老的<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"茶叶\" style=\"display: inline;\">茶叶</tuihighlight>，与新茶相对，尽管粗茶又苦又涩，但含有的茶多酚、茶丹宁等物质对身体有益。茶多酚是一种天然抗氧化剂，能抑制自由基在人体内造成的伤害，有抗衰老作用。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　它还能阻断亚硝胺等致癌物质对身体的损害。茶丹宁则能降低血脂，防止血管硬化，保持血管畅通，维护心、脑血管的正常功能。此外，茶多酚能缓解和减轻糖尿病症状，具有<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"降血脂\" style=\"display: inline;\">降血脂</tuihighlight>、降血压等作用。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　因此从营养角度来看，并非价格昂贵的新茶一定胜于价格低廉的粗茶和老茶，粗茶更适合老年人饮用。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　二、解读淡饭</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　淡饭并不是人们认为的粗粮、素食，这里有两层意思：一是淡而不咸，饮食过咸易引发骨质疏松、高血压，长期饮食过咸还可导致中风和心脏病;二是淡而不腻，既包含谷类食物和蔬菜，也应该包括脂肪含量低的鸡肉、鸭肉、鱼肉、牛肉等。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　也就是说，“粗茶淡饭”的正确饮用法是，以蔬菜等植物性食物为主，注意粮豆混食、米面混食，适当辅以包括肉类在内的各种动物性<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"食品\" style=\"display: inline;\">食品</tuihighlight>，常喝粗茶。由此，对于民间所流传的养生俗语，不要凭一知半解去实践，应考虑其中的科学含义。</span></p>', '', '', '1375464710', '249', '1', '', '', '', '3', '周边小编', '1'), ('9', '南宁外卖-桃子是“补气养血”佳品么', '', '<p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">桃子能预防便秘，同时也是补气养血佳果。但桃子再好也不能贪吃，否则易引起腹胀，还可能导致毛囊炎，也就是疖子。相对来说，内热的人不宜多吃桃。拿桃子当饭吃易上火，口干、口渴、严重的还会长疖子。对于上火引起的便秘，吃桃子反而会加重便秘。消化不良者、对桃毛过敏者以及<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"老人手机\" style=\"display: inline;\">老人</tuihighlight>、<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"儿童\" style=\"display: inline;\">儿童</tuihighlight>也不适合多吃桃。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative; text-align: center;\"><img class=\"conimg\" alt=\"套-350.jpg\" width=\"350\" height=\"300\" src=\"/data/upload/1h/7t/51fbedf80595a_600x480.jpg\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　吃前可用盐直接搓桃皮，再用水冲洗，能较干净地去除桃毛。如果是刚从树上摘下来的鲜桃，最好放半天等其暑气散尽再吃，一天吃桃最好不超过两个。</span></p>', '', '', '1375464957', '245', '1', '', '', '', '3', '周边小编', '1'), ('11', '喝茶不对会致命-周边网教你如何喝好茶', '', '<p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">春天是生产好茶的季节，也是品茶的好时光。要想喝上一杯上等好茶，除了具备好茶、好水的条件外，还必须掌握泡茶要点，才能喝得更健康。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;\"><span style=\"color:#666666;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"绿茶\" style=\"display: inline;\">绿茶</tuihighlight>，现泡现喝。</span></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"绿茶\" style=\"display: inline;\">绿茶</tuihighlight>之所以具有抗氧化、清除自由基、抗衰老、抗病毒等保健功能，主要是因为<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"茶叶\" style=\"display: inline;\">茶叶</tuihighlight>中的多酚类物质。如果冲泡温度过高或时间过久，多酚类物质就会被破坏，茶汤不但会变黄，其中的芳香物质也会挥发散失。一般来说，<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"绿茶\" style=\"display: inline;\">绿茶</tuihighlight>冲泡水温以80℃为宜，水初沸即可。冲泡时间以2—3分钟为好，最好现泡现饮。<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"绿茶\" style=\"display: inline;\">绿茶</tuihighlight>与水的比例要恰当，以1:50为宜，常用3克<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"茶叶\" style=\"display: inline;\">茶叶</tuihighlight>冲水150毫升，冲泡出来的<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"绿茶\" style=\"display: inline;\">绿茶</tuihighlight>汤浓淡适中。黄茶和白茶的冲泡方法与此接近。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><img class=\"conimg\" width=\"400\" height=\"310\" alt=\"喝茶不对致命！教你健康喝茶\" src=\"/data/upload/7m/8a/5221f40de453c_600x480.jpg\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;\"><span style=\"color:#666666;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 红茶，泡久些更健康。</span></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 与<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"绿茶\" style=\"display: inline;\">绿茶</tuihighlight>不同的是，高水温浸泡反而能够促进红茶内黄酮类保健物质的有效溶出，不但让滋味和香气更浓，还能更好发挥地它的保健功能。因而泡红茶最好用沸水，泡得久些更好。一般来说，5分钟最佳。红茶冲泡的用水量与<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"绿茶\" style=\"display: inline;\">绿茶</tuihighlight>相当。功夫红条茶可冲泡3次—4次，红碎茶则可冲泡1次—2次。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;\"><span style=\"color:#666666;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 乌龙茶，<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"茶叶\" style=\"display: inline;\">茶叶</tuihighlight>要多放。</span></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 乌龙茶冲泡时水温也以沸水为宜。乌龙茶的泡茶用量较多，一般以10克为宜，约装满<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"紫砂壶\" style=\"display: inline;\">紫砂壶</tuihighlight>容积的一半。乌龙茶可冲泡5—6次，时间可由短到长，以2—5分钟为宜。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;\"><span style=\"color:#666666;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 黑茶，重点在洗茶。</span></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 以<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"普洱茶\" style=\"display: inline;\">普洱茶</tuihighlight>为代表的黑茶，属于后发酵茶类，原料较为粗老。黑茶的特征在于“越陈越香”，须经历较为剧烈的沸水冲泡过程，才能使所含成分充分溶出。散茶容易出味儿，而紧压砖茶还要经过烹煮。第一次冲泡黑茶，要用10秒钟快速洗茶，即先把<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"茶叶\" style=\"display: inline;\">茶叶</tuihighlight>放入杯中，倒入开水，过一会儿把水倒掉，再倒入开水，盖上杯盖。这样不仅滤去了<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"茶叶\" style=\"display: inline;\">茶叶</tuihighlight>的杂质，而且更香醇。后续冲泡时间常为5分钟</span></p>', '', '', '1377956957', '1', '1', '', '', '', '3', '周边小编', '1'), ('12', '没人敢说我真会喝红酒', '', '<p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">&nbsp;喝<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"红酒\" style=\"display: inline;\">红酒</tuihighlight>不可以加冰，也不能像<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"香槟\" style=\"display: inline;\">香槟</tuihighlight>放在冰桶里，最适当的饮用温度是摄氏10~14度。广州的冬天，可以直接饮用;夏天，最好先放到<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"冰箱\" style=\"display: inline;\">冰箱</tuihighlight>的冷藏室，用餐前一至半小时取出，先开瓶，摆在一旁慢慢醒酒，同时让温度回升。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 你喜欢喝<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"红酒\" style=\"display: inline;\">红酒</tuihighlight>吗?近年来热衷于品尝<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"红酒\" style=\"display: inline;\">红酒</tuihighlight>的人士越来越多、但并不是每个热衷于<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"红酒\" style=\"display: inline;\">红酒</tuihighlight>的人都知道<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"红酒\" style=\"display: inline;\">红酒</tuihighlight>的正确喝法。我们来一起发掘一下<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"红酒\" style=\"display: inline;\">红酒</tuihighlight>里面的诸多学问吧!</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><img class=\"conimg\" border=\"0\" alt=\"你会喝红酒？不一定哦\" src=\"http://images.meishij.net/p/20120509/06bb87e75cdf65c8cb441cd6994b7d38.jpg\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;\">如何准备喝<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"红酒\" style=\"display: inline;\">红酒</tuihighlight>?</span></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"红酒\" style=\"display: inline;\">红酒</tuihighlight>平放着储放，软木塞浸泡日久会分解、产生木屑。饮用前一天要直立，让木屑沉淀到瓶底。注意看，<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"红酒\" style=\"display: inline;\">红酒</tuihighlight>的瓶底向中间凸起，这设计不是为了好拿酒瓶，而是要让木屑沈淀到凹沟。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 喝<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"红酒\" style=\"display: inline;\">红酒</tuihighlight>不可以加冰，也不能像<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"香槟\" style=\"display: inline;\">香槟</tuihighlight>放在冰桶里，最适当的饮用温度是摄氏10~14度。广州的冬天，可以直接饮用;夏天，最好先放到<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"冰箱\" style=\"display: inline;\">冰箱</tuihighlight>的冷藏室，用餐前一至半小时取出，先开瓶，摆在一旁慢慢醒酒，同时让温度回升。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;\">如何试酒?</span></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 先看酒的成色。为了正确观察，把<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"杯子\" style=\"display: inline;\">杯子</tuihighlight>放在白色背景的前方，微微向外倾斜<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"酒杯\" style=\"display: inline;\">酒杯</tuihighlight>。注意看是否有木屑或杂质，同时观察酒的颜色。咖啡色，坏了;紫红色，酒的年纪很轻;暗红，外围带褐黄色，好酒。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 再就是闻酒的味道。将杯口整个罩住鼻孔深呼吸。高级的<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"红酒\" style=\"display: inline;\">红酒</tuihighlight>闻起来味道很“厚”，让人感觉它很浓很复杂，由于鼻子的灵敏度远远超过舌头，因此有时候一杯酒慢慢品了一个小时，还是感觉香味越来越浓，浓到后来让人有点舍不得喝。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 闻完了，觉得味道很厚，接著试饮，浅浅一口，含在口中，用舌尖将酒液推向口腔的四周尽可能让所有味觉细胞都感受一下。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;\">如何倒酒？</span></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 高级<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"红酒\" style=\"display: inline;\">红酒</tuihighlight>的酒瓶都有人搜集，因为有的酒标简直就是一幅艺术品。为了避免酒液“流花”酒标，正确倒酒方法是让酒标的正面朝上此外。年代老的酒(超过8-10年)，瓶底一定有木屑，即使三五年，有的也有木屑。因此倒酒时要小心，除了不可晃动酒瓶，倒到最后，还要留一点在瓶的肩部。把酒瓶整个倒翻过来试图倒尽最后一滴酒，是不正确的。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;\">如何醒酒?</span></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"红酒\" style=\"display: inline;\">红酒</tuihighlight>开瓶以后，看酒的类型、品质，以及成熟程度，先静放15分钟至1个小时，让它与空气“呼吸”进行化学作用。假如没有耐性，可以直接倒进<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"酒杯\" style=\"display: inline;\">酒杯</tuihighlight>(依国际标准，不要超过<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"杯子\" style=\"display: inline;\">杯子</tuihighlight>容量的三分之一)，甚至晃动<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"酒杯\" style=\"display: inline;\">酒杯</tuihighlight>，加速酒与空气接触的机率。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 晃动<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"酒杯\" style=\"display: inline;\">酒杯</tuihighlight>的方法有两种，一是拿起<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"酒杯\" style=\"display: inline;\">酒杯</tuihighlight>向内摇晃，这种方法<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"杯子\" style=\"display: inline;\">杯子</tuihighlight>悬空，要晃动得均匀，得有点技术;另一种方法，是用食指中指夹住杯脚，整个手掌平贴杯底，将杯底压在桌面如同磨墨般地旋转，要小力一点，不要让酒洒出杯口。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;\">如何品酒?</span></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 昂贵的、好的<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"红酒\" style=\"display: inline;\">红酒</tuihighlight>是艺术品，不要“喝”，而是“品”。每次品之前，先晃动<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"酒杯\" style=\"display: inline;\">酒杯</tuihighlight>，再用鼻子深吸一下，然后浅尝一口，让酒液在口腔保留一段时间，之后才咽下去。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 注意，喝酒前请先将嘴吧擦干净，否则杯上将留有唇印。喝<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"葡萄酒\" style=\"display: inline;\">葡萄酒</tuihighlight>请勿干杯一口喝完。此外，喝<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"红酒\" style=\"display: inline;\">红酒</tuihighlight>不应加雪碧或柠檬，这会破坏<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"红酒\" style=\"display: inline;\">红酒</tuihighlight>原来的味道。摇着酒盅一杯一杯地比酒量不是<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"红酒\" style=\"display: inline;\">红酒</tuihighlight>的喝法。</span></p>', '', '', '1377957329', '3', '1', '', '', '', '3', '周边小编', '1'), ('13', '在民间小暑习俗吃三宝', '', '<p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">小暑节气吃什么?7月小暑，酷暑至，气温升高，三伏天就来了。这时小暑养生很重要，那么吃什么呢?民间小暑习俗吃三宝——黄鳝、蜜汁藕、<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"绿豆\" style=\"display: inline;\">绿豆</tuihighlight>芽，下面周边网小编带你看看小暑节气吃什么。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">一、小暑节气吃黄鳝</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　俗话说，“小暑黄鳝赛人参”，各地经典菜肴几乎都有以黄鳝为原料的。黄鳝生长在水岸泥窟之中，最滋补、最味美的莫属小暑前后一个月的夏鳝鱼。另外，这个时期往往是慢性支气管炎、支气管哮喘、风湿性关节炎等疾病的缓解期，根据冬病夏治的说法，此时用黄鳝滋补更能起到补中益气、补肝脾、除风湿、强筋骨的作用。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　推荐菜谱：&nbsp;酱烧黄鳝</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background-color: transparent; text-decoration: none;\"><img class=\"conimg\" src=\"/data/upload/7h/7a/5228a46b7401a_600x480.jpg\" alt=\"1.jpg\" width=\"400\" height=\"300\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　更多美味：豉椒黄鳝片　　韭黄烩黄鳝　　韭黄鳝糊</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">二、小暑节气吃莲藕</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　第二<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"大宝\" style=\"display: inline;\">大宝</tuihighlight>是营养又消暑的莲藕，在空气干燥、补水又不足的时候，常吃莲藕可以凉血、滋阴、清热，解决流鼻血这种麻烦可以说是小菜一碟。对老年人来说，夏藕更是补养脾胃的好食材。此外，莲藕也是高血压、肝病、食欲缺乏、缺铁性贫血、营养不良者的保健食物。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　推荐菜谱：&nbsp;桂花糯米藕</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background-color: transparent; text-decoration: none;\"><img class=\"conimg\" src=\"/data/upload/7t/7l/5228a58d913ac_600x480.jpg\" alt=\"2.jpg\" width=\"400\" height=\"300\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　更多美味：龙骨莲藕玉米汤　　回锅藕片　　香煎藕饼</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">三、小暑节气吃<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"绿豆\" style=\"display: inline;\">绿豆</tuihighlight>芽</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　小暑节气的第三<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"大宝\" style=\"display: inline;\">大宝</tuihighlight>是<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"绿豆\" style=\"display: inline;\">绿豆</tuihighlight>芽，炎炎夏日，烹制一道<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"绿豆\" style=\"display: inline;\">绿豆</tuihighlight>芽菜肴可以清热解毒、利尿除湿。同时，<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"绿豆\" style=\"display: inline;\">绿豆</tuihighlight>芽的热量很低，而水分和纤维素含量较高，可促进肠蠕动，具有通便的作用，是人们公认的夏季瘦身佳品，也是便秘患者的健康<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"蔬菜\" style=\"display: inline;\">蔬菜</tuihighlight>，对食道癌、胃癌、直肠癌患者也有良好的食疗价值。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　虽说<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"绿豆\" style=\"display: inline;\">绿豆</tuihighlight>芽的食用价值高，但食用起来也颇有讲究。<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"绿豆\" style=\"display: inline;\">绿豆</tuihighlight>皮不要随便去掉，要知道，这层不起眼的<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"绿豆\" style=\"display: inline;\">绿豆</tuihighlight>皮具有比<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"绿豆\" style=\"display: inline;\">绿豆</tuihighlight>还要强的清热解毒功效。另外，<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"绿豆\" style=\"display: inline;\">绿豆</tuihighlight>芽性偏寒凉，吃多了容易损伤胃气，因此，慢性肠炎、脾胃虚寒、慢性胃炎者不宜多吃。不过烹调时配上一点姜丝或椒丝，或是搭配些偏温的香菜、韭黄，就能很好地中和掉它的寒性了。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　推荐菜谱：凉拌豆芽</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background-color: transparent; text-decoration: none;\"><img class=\"conimg\" src=\"/data/upload/7b/5e/5228a495c3739_600x480.jpg\" alt=\"3.jpg\" width=\"400\" height=\"300\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></span></p>', '', '', '1378395342', '84', '1', '', '', '', '3', '周边小编', '1');
INSERT INTO `articles` VALUES ('14', '帮男人吃出来的腹肌块', '', '<p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">低碳水化合物饮食看起来很简单，但使用不当却十分危险。因为，当身体缺乏足够的糖元时，就会转而使用蛋白质储备作为能源。下面3个窍门能帮助你在低碳水化合物饮食期间保持肌肉块。</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative; text-align: center;\"><img class=\"conimg\" height=\"300\" alt=\"正文.jpg\" width=\"350\" src=\"/data/upload/0g/9t/52299a7079569_600x480.jpg\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　1、提高蛋白质摄入量</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　低碳水化合物饮食要求按照体重比例来限制碳水化合物摄人量：体重190磅以上的健美运动员应限制在每天56—75克碳水化合物，体重190磅以下的运动员应限制在每天40—55克碳水化合物。当碳水化合物摄入低于每天75克时，身体将把更多的蛋白质作为能源。因此，在低碳水化合物摄入期间应把蛋白质摄人量提高到每天每磅体重2克。</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　2、训练前后摄入乳清蛋白质</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　乳清蛋白中含有大量的支链氨基酸，它们能替代碳水化合物为训练提供能量。训练前摄人40—60克乳清蛋白质，可防止身体消耗肌肉组织供能。训练后立即摄入另40—60克乳清蛋白质，可重建肌肉组织。另外，把每日碳水化合物定额的50%安排在训练后摄入。</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　3、每天摄入红色肉类</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　红色肉类能提供脂肪给身体作为能源，使蛋白质免遭用于供能的噩运。红色肉类还富含丙胺酸，它能用于供能而不引起胰岛素水平升高。每天至少从红色瘦肉中摄取50克蛋白质，把它分配在两餐中：早餐25克，另一餐25克。这样可保证体内持续的丙胺酸供应，防止身体将蛋白质作为能源。</p><br />', '', '', '1378458414', '94', '1', '', '', '', '3', '周边小编', '1'), ('15', '秋天季节水果该怎么吃', '', '秋天燥气当令，应多吃含水丰富、有滋润作用的水果。应季的水果,对人体有哪些保健作用呢?我们该以什么样的正确方法食用呢?<p style=\"TEXT-ALIGN: center\">&nbsp;</p><p style=\"TEXT-ALIGN: center\"><img class=\"conimg\" alt=\"正文.jpg\" src=\"/data/upload/3w/8c/522ac999cdb8e_600x480.jpg\" width=\"350\" height=\"300\" /></p><p>　　1、梨</p><p>　　梨味甘性凉、微酸，具有清热生津、润肺化痰的功效，是防治秋燥的佳果。梨能维持细胞组织的健康状态，还能软化血管。生食梨有清热解毒、生津润燥、清心降火作用;煎水或加蜜熬膏，有清热润肺、化痰止咳的作用。儿童咳嗽可以制作核桃糖梨饮，取生梨1个(约150克)切成大块，核桃仁(保留紫衣)、冰糖各30克，加水800毫升，煮成汁。每次吃一汤匙，每天3次，连服数日。咽喉肿痛、咳嗽可以吃蜂蜜雪梨，用雪花梨3只，捣烂后加蜂蜜50克，水煎服用，每日3次。梨性寒，寒凉体质、脾虚腹泻者忌食。</p><p>　　2、葡萄</p><p>　　葡萄具有补益气血、强筋骨、通经络、消肿、利小便的功效，适合贫血、高血压、水肿、神经衰弱、疲劳的人吃。葡萄含铁量较高，也适合儿童、妇女、体弱贫血者食用。高血压的人可以经常喝葡萄芹菜汁：葡萄50克、芹菜50克，洗净后放入榨汁机里榨汁，用温开水送服，每日2～3次，20天为1个疗程。年轻女士们如果食欲不振，早餐时可以在250毫升鲜牛奶中加入20克葡萄干，一起煮开。脾胃虚弱者不宜多食葡萄;吃葡萄后也不能立刻喝水，否则容易腹泻;糖尿病患者不可多吃，每天最好不超过20粒。</p><p style=\"TEXT-ALIGN: center\">&nbsp;</p><p style=\"TEXT-ALIGN: center\"><img class=\"conimg\" alt=\"正文2.jpg\" src=\"/data/upload/3b/2n/522ac976a0f90_600x480.jpg\" width=\"457\" height=\"299\" /></p><p>　　3、石榴</p><p>　　石榴性温，味甘、酸、涩，具有生津止渴、收敛固涩、止泻止血的功效，适用于津亏口燥咽干、烦渴、久泻、久痢、便血、白带清稀频多等症状。石榴汁中多酚的含量较高，具有很好的抗衰、防癌作用。腹泻、月经不调的人平时可以多吃石榴粳米粥，准备甜石榴1个，粳米50克，白糖适量，将石榴去皮捣碎，放入锅中，加水煮沸15分钟后，过滤去渣，取汤汁，放入粳米，大火煮沸后，转小火熬煮成粥，调入白糖即可。醉酒后口渴，不妨喝点石榴汤：鲜石榴2个，冰糖适量，石榴去皮放入锅中，加适量水煮半小时，放入冰糖煮至溶化即可。石榴不可多食，否则损齿、伤肺;石榴不宜与海鲜、鱼类同食;石榴有收敛作用，患有感冒、急性炎症、便秘者慎食。</p><p>　　4、苹果</p><p>　　苹果味甘、性凉，具有生津止渴、清热除烦、健胃消食、润肺悦心、涩肠止泻之功效。苹果可降低胆固醇、防动脉硬化、抗癌、抗疲劳，还有助于将钠排出体外。高血压病引起的眩晕失眠、厌食疲倦及轻度泄泻等，可以早餐前服用苹果桂花羹：苹果1个(去皮核切片)，桂花少许、冰糖、淀粉各适量;用水煮苹果，将熟时加入淀粉、冰糖、桂花调匀成羹服用。小儿消化不良所致腹泻，可用苹果泥治疗。准备几个苹果，用开水洗净，削皮，隔水蒸熟，捣烂成泥;每日4次，每次约100克，一岁以下婴儿每次50克，日服3～4次。由于苹果富含鞣酸，可能引起便秘而加重病情，心肌梗死及糖尿病人不宜多吃;平时有胃寒症状者、溃疡性结肠炎的病人忌生食苹果。</p><p>　　5、山楂</p><p>　　山楂味甘酸、微温，有散淤、消积、化痰、解毒、提神、清胃、醒脑、增进食欲、除脂降压等功效。山楂营养丰富，维生素C和钙含量都很高，适合高血压、冠心病、糖尿病患者及儿童食用。小儿消化不良，可用炒麦芽山楂饮，生山楂20克，去核切片，同10克炒麦芽共用开水浸泡，随量饮用。山楂味酸，故胃出血、胃酸过多的人忌食;山楂可促进宫缩，孕妇禁食。</p>', '', '', '1378535924', '80', '1', '', '', '', '3', '周边小编', '1'), ('16', '炎热夏天，饮食知多少', '', '<p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">俗话说“小暑不算热，大暑三伏天”。高温和潮湿是大暑时节的主要气候特点。大暑期间饮食要特别注意。这时市民可多吃消暑清热、化湿健脾的<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"食品\" style=\"display: inline;\">食品</tuihighlight>。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　所谓“天生万物以养民”。大暑期间，市民应该多吃丝瓜、西兰花和茄子等当季蔬菜。大暑天气酷热，出汗多，脾胃活动相对较差。这时人会感觉比较累和食欲不振。而淮山有补脾健胃，益气补肾作用。多吃淮山一类益气养阴的<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"食品\" style=\"display: inline;\">食品</tuihighlight>，可以促进消化，改善腰膝酸软，使人感到精力旺盛。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><img class=\"conimg\" src=\"/data/upload/3i/9m/522c967324803_600x480.jpg\" alt=\"1.jpg\" width=\"400\" height=\"300\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　胡萝卜、<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"红枣\" style=\"display: inline;\">红枣</tuihighlight>等红色食物对心脏还很有益，夏季适宜养心，应该多吃。俗语说“冬吃萝卜夏吃姜”。吃姜有助于驱除体内寒气，大家可以尝试一下子姜炒牛肉、子姜炒木耳等菜式。但吃姜的时间也有讲究，最好不要在晚上吃。冬补三九，夏补三伏。家禽肉的营养成分主要是蛋白质，其次是脂肪、微生物和矿物质等，相对于家畜肉而言，是低脂肪高蛋白的食物，其蛋白质也属于优质蛋白。鸡、鸭、鸽子等家禽都是大暑进补的上选。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　民间有一传统的进补方法，就是大暑吃童子鸡。童子鸡，是指还不会打鸣，生长刚成熟但未配育过的小公鸡;或饲育期在三个月内体重达一斤至一斤半、未曾配育过的小公鸡，后来也有专门的品种称为童子鸡。童子鸡体内含有一定的生长激素，对处于生长发育期的孩子以及激素水平下降的中老年人都有很好的补益作用。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">推荐<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"美食\" style=\"display: inline;\">美食</tuihighlight>：虫草炖童子鸡</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background-color: transparent; text-decoration: none;\"><img class=\"conimg\" src=\"/data/upload/2v/7a/522c969cdc169_600x480.jpg\" alt=\"2.jpg\" width=\"400\" height=\"300\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　鸭是一种营养丰富的水禽。每100克鸭肉含蛋白质16.5克，脂肪7.5克，还含有钙、磷、铁、多种维生素及碳水化合物等营养成分。因其常年在水中生活，性偏凉，有滋五脏之阳、清虚劳之热、补血行水、滋阴养胃、利水消肿的功效。尤其是经过一个冬春摄食的老鸭，骨骼更健壮，肌肉更丰满。可主治水肿胀满、阴虚失眠、疮毒、惊痫等症。《名医别录》中称鸭肉为“妙药”和滋补上品。民间亦有“大暑老鸭胜补药”的说法。老鸭炖食时可加入莲藕、冬瓜等蔬菜煲汤食用，能补虚损、消暑滋阳。加配芡实、薏苡仁同炖汤，滋阳效果更佳，能健脾化湿、增进食欲。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">推荐<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"美食\" style=\"display: inline;\">美食</tuihighlight>：子姜焖鸭</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background-color: transparent; text-decoration: none;\"><img class=\"conimg\" src=\"/data/upload/3v/8j/522c96b1074e2_600x480.jpg\" alt=\"3.jpg\" width=\"400\" height=\"300\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　鸽肉性平，具有补肝肾、益气血、祛风解毒的功效，可气血双补，还有安神的作用，特别适合脑力劳动者、神经衰弱者进补。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">推荐<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"美食\" style=\"display: inline;\">美食</tuihighlight>：白莲茶树菇乳鸽汤</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background-color: transparent; text-decoration: none;\"><img class=\"conimg\" src=\"/data/upload/0k/5p/522c97143350d_600x480.jpg\" alt=\"4.jpg\" width=\"400\" height=\"300\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　仙草又名凉粉草、仙人草，唇形科仙草属草本<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"植物\" style=\"display: inline;\">植物</tuihighlight>，为重要的药食两用<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"植物\" style=\"display: inline;\">植物</tuihighlight>资源。由于其神奇的消暑功效，被誉为“仙草”。茎叶晒干后可以做成烧仙草，广东一带叫凉粉，是一种消暑的甜品。本身也可入药。民谚：六月大暑吃仙草，活如神仙不会老。烧仙草是台湾著名的<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"小吃\" style=\"display: inline;\">小吃</tuihighlight>之一，有冷、热两种吃法。烧仙草的外观和口味均类似粤港澳地区流行的另一种<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"小吃\" style=\"display: inline;\">小吃</tuihighlight>龟苓膏，也同样具有清热解毒的功效。但这款<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"食品\" style=\"display: inline;\">食品</tuihighlight><tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"孕妇\" style=\"display: inline;\">孕妇</tuihighlight>忌吃。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">推荐<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"美食\" style=\"display: inline;\">美食</tuihighlight>：烧仙草&nbsp;</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background-color: transparent; text-decoration: none;\"><img class=\"conimg\" src=\"/data/upload/1s/4b/522c973651c96_600x480.jpg\" alt=\"5.jpg\" width=\"400\" height=\"300\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></span></p>', '', '', '1378654095', '40', '1', '', '', '', '3', '周边小编', '1'), ('17', '胃的治与养，三七分。', '', '<p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">常言道：十人九胃。意思是说胃病很常见。人们每天要进食，但有的人或五味偏嗜、或暴饮暴食、或嗜酒无度、或服用中西药物不当，皆可损伤胃气，导致胃病，表现为胃痛、胃胀、嗳气、泛酸、食欲不振，真是恶心、呕吐等症状。现在正值秋季，天气渐渐偏凉，胃病易多发与复发，胃肠道对寒冷的刺激非常敏感，如果防护不当，就会引发胃肠道疾病或使原有的胃病加重。那么，秋季该如何在饮食上养胃护胃呢?</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative; text-align: center;\"><img class=\"conimg\" height=\"320\" alt=\"文章最大图.jpg\" width=\"500\" src=\"http://images.meishij.net/p/20130905/82eeff9547071333566966454a3daaad.jpg\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　<span style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;\">　一、秋季养胃护胃5要领</span></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　<span style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;\">1、保暖</span></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative; text-align: center;\"><img class=\"conimg\" height=\"235\" alt=\"静心.jpg\" width=\"360\" src=\"/data/upload/1l/1z/522df9817e50e_600x480.gif\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　患有慢性胃炎的人，要特别注意胃部的保暖，适时增添衣服，夜晚睡觉盖好被褥。另外，胃病患者&quot;秋冻&quot;要适度，不要勉强。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　<span style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;\">　2、调养</span></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　胃病患者的秋季饮食应以温、软、淡、素、鲜为宜，做到定时定量，少食多餐，使胃中经常有食物和胃酸进行中和，从而防止侵蚀胃粘膜和溃疡面而加重病情。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　<span style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;\">3、忌嘴</span></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative; text-align: center;\"><span style=\"color:#666666;\">&nbsp;</span><img src=\"/data/upload/4s/7d/522df990db75a_600x480.jpg\" alt=\"\" /></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　胃病患者要注意忌嘴，不吃过冷、过烫、过硬、过辣、过粘的食物，更忌暴饮暴食，戒烟禁酒。另外，服药时应注意服用方法，最好饭后服用，以防刺激胃粘膜而导致病情恶化。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　<span style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;\">4、静心</span></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　胃病、十二指肠溃疡等症的发生与发展，与人的情绪、心态密切相关。因此，要讲究心理卫生，保持精神愉快和情绪稳定。避免紧张、焦虑、恼怒等不良情绪的刺激。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　<span style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;\">5、运动</span></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative; text-align: center;\"><img class=\"conimg\" height=\"235\" alt=\"运动.jpg\" width=\"360\" src=\"/data/upload/0o/4l/522df9a0aef3a_600x480.jpg\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　肠胃病人要结合自己的体征，加强适度的运动锻炼，提高机体抗病能力，减少疾病的复发，促进身心健康。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;\"><span style=\"color:#666666;\">二、6大食物健脾养胃效果佳</span></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　<span style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;\">1、粥类</span></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative; text-align: center;\"><span style=\"color:#666666;margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background-color: transparent; text-decoration: none;\"><img class=\"conimg\" height=\"235\" alt=\"粥.jpg\" width=\"360\" src=\"/data/upload/0f/2t/522df9b13f130_600x480.jpg\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　粥类容易消化，尤其八宝粥，里面含有花生、杏仁、白糖等，热量更高更丰富，对胃有好处，而新鲜的花生富含蛋白质、脂肪，能有效地保护胃。还有就是小米粥，营养价值丰富，具有健胃消食、防止反胃、呕吐的功效，有“代参汤”之美称。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　<span style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;\">2、山药</span></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative; text-align: center;\"><span style=\"color:#666666;margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background-color: transparent; text-decoration: none;\"><img class=\"conimg\" height=\"235\" alt=\"山药.jpg\" width=\"360\" src=\"/data/upload/2q/9k/522df9c29e896_600x480.jpg\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　中医认为，山药味甘、性平，具有健脾益胃，补肺固肾等作用。适用于脾虚久泄、食欲不振、倦怠无力等病症。现代药理学研究发现，山药含淀粉酶、多酚氧化酶等，有利于肠胃消化吸收功能，适宜于慢性胃炎、肠炎，糖尿病及病后虚弱者。山药有收涩作用，大便燥结及邪实者不宜食用。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　<span style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;\">　3、卷心菜</span></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative; text-align: center;\"><span style=\"color:#666666;margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background-color: transparent; text-decoration: none;\"><img class=\"conimg\" height=\"235\" alt=\"卷心菜.jpg\" width=\"360\" src=\"/data/upload/8c/2b/522df9cfd99d4_600x480.jpg\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　卷心菜有健脾养胃、缓急止痛、解毒消肿、清热利水的作用，可用于内热引起的胸闷、口渴、咽痛、小便不通、耳目不聪、睡眠不佳、关节不利和腹腔隐痛等症。其含有的维生素C等成分具有止痛及促进溃疡愈合的作用。维生素U具有保护黏膜细胞的作用，据说对胃火及胃溃疡的预防与治疗有较好的效果。卷心菜与薏米、陈皮、蜂蜜同煨可用于治疗胃脘胀痛、上腹胀满及胃、十二指肠溃疡，与赤小豆、冬瓜、冰糖煨熟可消肿利水，大便溏泻及脾胃虚弱者不宜多吃。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　<span style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;\">　4、南瓜</span></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative; text-align: center;\"><span style=\"color:#666666;margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background-color: transparent; text-decoration: none;\"><img class=\"conimg\" height=\"235\" alt=\"南瓜.jpg\" width=\"360\" src=\"/data/upload/2u/3r/522df9f0e93d4_600x480.jpg\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　《本草纲目》载：&quot;南瓜性温，味甘，入脾、胃经&quot;，能补中益气、消炎杀菌、止痛。其所含的丰富果胶，可&quot;吸附&quot;细菌和有毒物质，包括重金属、铅等，起到排毒作用。同时，果胶可保护胃部免受刺激，减少溃疡。可用南瓜煮粥或汤，滋养肠胃。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　<span style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;\">5、芝麻</span></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative; text-align: center;\"><span style=\"color:#666666;margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background-color: transparent; text-decoration: none;\"><img class=\"conimg\" height=\"235\" alt=\"芝麻.jpg\" width=\"360\" src=\"/data/upload/3t/9o/522df9ffdd01b_600x480.jpg\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　芝麻味甘、性平，入肝、肾、肺、脾经，有补血明目、祛风润肠、生津通乳、益肝养发、强身体、抗衰老的作用。《神农本草经》中说，芝麻能够“补五脏、益气力、长肌肉、填精益髓”。芝麻中的维生素E能防止过氧化脂质对皮肤的危害，多食用一些芝麻会使皮肤变得细腻光滑、红润光泽。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　<span style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;\">　6、酸奶<span style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;\"><span style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;\">（点击图片可进入食材详细介绍与菜谱做法）</span></span></span></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative; text-align: center;\"><span style=\"color:#666666;margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background-color: transparent; text-decoration: none;\"><img class=\"conimg\" height=\"235\" alt=\"酸奶.jpg\" width=\"360\" src=\"/data/upload/7h/6f/522dfa0e6fe6e_600x480.jpg\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">　　酸奶除含有牛奶的全部营养素外，突出的特点是含有丰富的乳酸，能将奶中的乳糖分解为乳酸。对于胃肠道缺乏乳酸酶或喝鲜牛奶容易腹泻的人，可改喝酸奶。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 乳酸能抑制体内霉菌的生长，可预防使用抗菌素类药物所导致的菌群失调。乳酸还可以防止腐败菌分解蛋白质产生的毒物堆积，因而有防癌作用。</span></p><br />', '', '', '1378744944', '70', '1', '', '', '', '3', '周边小编', '1'), ('18', '结账，就该符合您身份', '', '<p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">&nbsp; &nbsp; &nbsp; &nbsp; 在餐厅用餐完毕，如何大大方方地结帐，留给你的同伴和服务人员一个好印象，也是重要的餐饮礼节之一。 通常说来，用餐完毕准备离去时，要利用服务人员经过你身边的机会，轻声唤住他，很有礼貌的告诉他：“请帮我们结帐。”如果一时没有服务人员走近，不妨耐心地多等一、两分钟。</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; position: relative;\"><span style=\"color:#666666;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 有许多人，吃饭、菜可以吃一、两个小时，结帐等一、二分钟却不耐烦，往往四周没有服务人员，便提高噪门大叫买单，或者手握钞票，举得高高的挥来挥去。 之所以有这样的反应，是因为自认为自己是消费者，理所当然可以这么做。但是，我必须提醒这些朋友一点，坐在你<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"餐桌\" style=\"display: inline;\">餐桌</tuihighlight>四周其他桌的客人，他们也是消费者，如果你大声吼叫，是不是影响了其他人用餐的情趣与安宁呢？ 用完餐结帐还有一点需要注意，那就是结帐的工作，绝对是<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"男士衬衫\" style=\"display: inline;\">男士</tuihighlight>的专利。即使你们这次是由<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"女士包包\" style=\"display: inline;\">女士</tuihighlight>请客，或男男女女大家平均分摊消费额，<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"女士包包\" style=\"display: inline;\">女士</tuihighlight>亦应将钱交给<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"男士衬衫\" style=\"display: inline;\">男士</tuihighlight>，由<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"男士衬衫\" style=\"display: inline;\">男士</tuihighlight>招请服务人员结帐。此一习惯乃是餐饮的基本规则，千万不要逾越，否则你们的关系和在座<tuihighlight class=\"tuihighlight\" data_cnzz_tui=\"女士包包\" style=\"display: inline;\">女士</tuihighlight>所从事的职业，会让人有不健康的联想。 此外，除非餐厅有特别的规定，否则一般来说，买单应该坐在自己位子上买。因为跑到柜台前面掏出钱来结帐，既不雅观，也不合乎餐厅礼节的规定。</span></p>', '', '', '1378917792', '68', '1', '', '', '', '3', '周边小编', '1'), ('19', '周边道出教师节的历史', '', '<p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">教师节，是我国仅有的包括护士节、记者节在内的三个行业性节日。自1931年以来，我国在不同历史时期共有过4种不同日期和性质的教师节。</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　我国历史上最早出现的教师节是1931年。当时，教育界知名教授邰爽秋、程其保等，发起联络京、沪教育界人士，拟定每年6月6日为教师节，并发表《教师节宣言》，提出改善教师待遇、保障教师工作、增进教师修养三项目标。虽然，这个教师节的诞生当时的国民党政府没有承认，但在全国各地产生了一定影响。鉴于“六·六”教师节是教师自发组织设立的，国民党政府没有承认，1939年决定另立孔子诞辰日8月27日为教师节，并颁发了《教师节纪念暂行办法》，当时未能在全国推行。</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　1951年，中华人民共和国教育部和中华全国总工会共同商定，把5月1日国际劳动节也作为教师节，由于各种原因，教师节实际上逐渐销声匿迹了。&quot;我也不知道为什么，那天早上一起床就忽然想到老师应该有自己的节日。&quot;教师节的最早创意人、中科院院士王梓坤教授这样说。</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　那是1984年12月9日，王教授当天就把这个想法告诉了《北京晚报》。第二天，《北京晚报》就刊出文章《王梓坤校长建议开展尊师重教月活动》，引起了读者强烈反响。12月15日，北师大钟敬文、启功、王梓坤、陶大镛、朱智贤、黄济、赵擎寰联名，正式提议设立教师节。1985年1月21日，第六届全国人大常委会第九次会议作出决议，将每年的9月10日定为教师节。</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　为了发扬“尊师重教”的优良传统，提高教师地位，1985年1月21日第六届全国人大常委会第九次会议确定，每年9月10日为中国教师节。在第一个教师节，当时的国家主席李先念向全国教师发出慰问信祝贺节日，首都召开万人庆祝大会，教师节期间20个省市共表彰11871个省级优秀教师集体和个人。</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　其实早在1932年，国民党政府曾规定6月6日为教师节，解放后废除了6月6日的教师节，改用&quot;五一国际劳动节&quot;同时为教师节，但教师没有单独活动，没有特点。而将教师节定在9月10日是考虑到全国大、中、小学新学年开始，学校要有新的气象。新生入学伊始，即开始尊师重教，可以给&quot;教师教好、学生学好&quot; 。</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\"><img class=\"conimg\" src=\"/data/upload/4b/2d/5231f1f24064d_600x480.jpg\" alt=\"2.jpg\" width=\"450\" height=\"342\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></p>', '', '', '1379004941', '57', '1', '', '', '', '3', '周边小编', '1'), ('20', '酒饭桌讲话需知的礼仪', '', '<p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">谈起喝酒，几乎所有的人都有过切身体会，&quot;酒文化&quot;也是一个既古老而又新鲜的话题。现代人在交际过程中，已经越来越多地发现了酒的作用。的确，酒作为一种交际媒介，迎宾送客，聚朋会友，彼此沟通，传递友情，发挥了独到的作用，所以，探索一下酒桌上的&quot;奥妙&quot;，有助于你求人交际的成功。</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\"><img class=\"conimg\" src=\"/data/upload/5k/0b/5235e621a60a4_600x480.jpg\" alt=\"2.jpg\" width=\"400\" height=\"300\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\"><span style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;\">1、众欢同乐，切忌私语</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　大多数酒宴宾客都较多，所以应尽量多谈论一些大部分人能够参与的话题，得到多数人的认同。因为个人的兴趣爱好、知识面不同，所以话题尽量不要太偏，避免唯我独尊，天南海北，神侃无边，出现跑题现象，而忽略了众人。　　　　　特别是尽量不要与人贴耳小声私语，给别人一种神秘感，往往会产生&quot;就你俩好&quot;的嫉妒心理，影响喝酒的效果。</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\"><span style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;\">2、瞄准宾主，把握大局</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　大多数洒宴都有一个主题，也就是喝酒的目的。赴宴时首先应环视一下各位的神态表情，分清主次，不要单纯地为了喝酒而喝酒，而失去交友的好机会，更不要让某些哗众取宠的酒徒搅乱东道主的意思。</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\"><span style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;\">3、语言得当，诙谐幽默</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　洒桌上可以显示出一个人的才华、常识、修养和交际风度，有时一句诙谐幽默的语言，会给客人留下很深的印象，使人无形中对你产生好感。所以，应该知道什么时候该说什么话，语言得当，诙谐幽默很关键。</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\"><span style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;\">4、劝酒适度，切莫强求</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\"><img class=\"conimg\" src=\"/data/upload/6b/7s/5235e61310b9a_600x480.jpg\" alt=\"1.jpg\" width=\"400\" height=\"300\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　在酒桌上往往会遇到劝酒的现象，有的人总喜欢把酒场当战场，想方设法劝别人多喝几杯，认为不喝到量就是不实在。</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　&quot;以酒论英雄&quot;，对酒量大的人还可以，酒量小的就犯难了，有时过分地劝酒，会将原有的朋友感情完全破坏。</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\"><span style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;\">5、敬酒有序，主次分明</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　敬酒也是一门学问，一般情况下敬酒应以年龄大小、职位高低、宾主身份为序，敬酒前一定要充分考虑好敬酒的顺序，分明主次。好使与不熟悉的人在一起喝酒，也要先打听一下身份或是留意别人如何称呼，这一点心中要有数，避免出现尴尬或伤感情的局面。</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　敬酒时一定要把握好敬酒的顺序，有求于某位客人在席上时，对他自然要倍加恭敬，但是要注意，如果在场有更高身份或年长的人，则不应只对能帮你忙的人毕恭毕敬，也要先给尊者长者敬酒，不然会使大家都很难为情。</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\"><span style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;\">6、察言观色，了解人心</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　要想在酒桌上得到大家的赞赏，就必须学会察言观色。因为与人交际，就要了解人心，左右逢源，才能演好酒桌上的角色。</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\"><span style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;\">7、锋芒渐射，稳坐泰山</span></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　酒席宴上要看清场合，正确估价自己的实力，不要太冲动，尽量保留一些酒力和说话的分寸，既不让别人小看自己又不要过分地表露自身，选择适当的机会，逐渐放射自己的锋芒，才能稳坐泰山，不致给别人产生&quot;就这点能力&quot;的想法，使大家不敢低估你的实力。&nbsp;</p>', '', '', '1379264039', '106', '1', '', '', '', '3', '周边小编', '1'), ('21', '各地方中秋的饮食风俗', '', '<p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\"><img class=\"conimg\" src=\"http://images.meishij.net/p/20130916/78be9e387c27de8ebc6fdc85e4b38f16.jpg\" alt=\"500.jpg\" width=\"500\" height=\"375\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; display: block; max-width: 550px;\" /></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">一、浙江</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　杭州的莼菜鲈鱼烩之所以成为中秋家宴上的菜肴，不仅仅是因为这一时节的莼菜鲈鱼好吃，更是因为晋代张翰借思乡的“莼菜、鲈鱼”，弃官返回故里的史实，这一故事不仅成为千古美谈，并使莼菜成为思乡的象征。莼菜是中秋家宴和八月时令菜羹。莼菜又称马蹄草、水菜，是水生宿根生叶草植物。莼菜的根、茎、叶不仅碧绿清香，鲜嫩可口，而且营养丰富。</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">二、北京</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　老北京的传统月饼有自来红、自来白和提浆月饼。提浆月饼是指月饼皮面的一种制作方法，说通俗点“提浆”就是熬糖浆，另外在皮面中还要加入一定比例的大油，以起到酥松的作用。有回民购买时，就把大油换成黄油。这种月饼的特点是皮酥、馅香，“老北京”中秋特爱吃。菜式上，八月秋高蟹正肥，中秋正是品尝螃蟹的时节，北京尤其讲究品尝醉蟹。</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">三、陕西</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　陕西西安一带，每到中秋节，当地每家做馍，全家共吃一馍，称“团圆馍”。馍有顶、底两层，中间加芝麻。馍上层用大碗拓一个圆圈，象征中秋之夜，圆中刻“石头”一块，上站一个吃蟠桃的“小猴子”。馍在锅里烙熟后，切成尖牙状，全家每人一牙，家人短期外出，则留下一牙。出嫁的姑娘，娘家则送馍上门，以示全家团圆不忘。</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　中秋佳节，陕西人不论贫富，必食西瓜，西瓜还要切成莲花状，它作为解暑良品，性寒凉，糖多，能清热泻火，具有宽中下气、利尿、治血痢、解酒毒的功效。在中秋月圆之时，一边乘凉赏月，一边吃着解暑的西瓜，凉意宜人，更增一家团聚的天伦之乐。而且，吃月饼之余吃上块西瓜，清热解腻。久而久之，吃西瓜成为了陕西人过中秋的习惯，所以当今陕西人中秋都会吃西瓜。而西瓜切成莲花状，只是为了配合中秋的节日气氛，增加喜庆吉祥。</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">四、四川</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　四川人过中秋除了吃月饼外，还要打粑、吃麻饼、蜜饼等，四川人也在中秋节杀鸭子。在川西地区，烟熏鸭子是中秋节必备佳品，因那时当年生鸭已长大，肥瘦适宜。师傅选当年生的仔鸭，宰杀后褪尽羽毛，开膛取出内脏，洗净后，去翅尖、鸭脚，加盐码味腌渍一夜后，入沸水中略烫至皮紧，捞出抹干水分，置熏炉中，用稻草烟熏至呈茶色，出炉放入卤锅中卤熟，食时改刀装盘，色泽金红、肉质细嫩、烟香浓郁的烟熏鸭即成。</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img src=\"/data/upload/3x/2t/52388debcb6c1_600x480.jpg\" alt=\"\" /><br /></p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">五、山西</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　山西三晋的月牙月饼。山西月饼多以油和面，馅子有红枣、核桃、豆类、柿子、果脯、红糖等种类。其中，繁峙县有一种特殊的球形月饼，是当地农家的独特创造。一般农家打月饼时，还专门为孩子们打制一些兔形小月饼，希望他们将来能够“蟾宫折桂”，步入仕途。</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">六、江苏</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　南京人中秋爱吃月饼外，必吃金陵名菜桂花鸭。“桂花鸭”于桂子飘香之时应市，肥而不腻，味美可口。酒后则必食桂花鸭一小糖芋头，浇以桂浆，美不待言。桂浆又叫糖桂花，中秋前后采摘，用糖及酸梅腌制而成。江南妇女手巧，把诗中的咏物变为桌上佳肴。南京人合家赏月称“庆团圆”，团坐聚饮叫“圆月”，出游街市称“走月”。在江苏东台一带，每逢中秋节，则以藕横断为饼，包以碎肉，敷以小麦屑，入油锅炸熟，谓之藕饼，俗称“荷花”，寓意和睦团圆。</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">七、广东</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　潮汕中秋美食品种颇多，主要可分为三大类：一是糕饼类，潮汕各地月饼，甜的、咸的、荤的、什料的、多味的各式各样种类繁多。还有面饼、软糕、云片糕均为中秋节糕饼，为潮人送亲戚之佳品，可说潮人送月饼是睦亲的习俗;二是潮地中秋节季，天高气爽正是水果成熟之时，柚、柿、杨桃、菠萝、石榴、橄榄、香蕉等也是潮人中秋的另一类美食;三是农产的芋头、南瓜，潮人用其制作芋泥、瓜浆也是潮人喜欢的。芋头、糍粑等也是潮人中秋必吃的。</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">八、台湾</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　高雄县饲养水鸭的风气很盛。中秋节前后正是水鸭公初长最嫩的时候，美浓地区的客家人往往于中秋节宰食水鸭公加菜，成为当地中秋的特色。宜兰地区中秋节除了吃月饼外，还吃一种叫“菜饼”的食物。菜饼以面粉为材料，中间抹上黑糖烘焙而成。台南地区则有在中秋节吃麻薯的习俗。</p><p style=\"margin: 0px; padding: 10px 20px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 24px; color: rgb(102, 102, 102); position: relative;\">　　此外，安徽婺源地区中秋必食塘鱼;云南昆明在中秋节时每户必做“合家大月饼”，然后一人一块分而食之；山东泰安要吃小包子;仡佬族人在中秋前全寨合伙宰一头公牛，取出牛心留到中秋节晚祭祀祖先，然后分吃牛肉。</p>', '', '', '1379438072', '266', '1', '', '', '', '3', '周边小编', '1'), ('22', '月饼也能吃出健康来的', '', '<p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">&nbsp; &nbsp;　<strong>中秋团圆让人欢喜，月饼却让人头疼</strong></p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">　　又快到中秋了，一家人团圆让人欢喜，可是这伴随而来的月饼似乎就没有这么让人喜欢。“年年中秋节送月饼，血糖高不敢吃，无糖月饼又不是特别放心”，“每年单位发的月饼都没人吃”，有人还坦露心声，不希望今年再收到月饼。退休教师韩建柯说，现在月饼情节一年比一年淡，送出去的月饼来回在亲朋之间传递，处理月饼让人头疼。“月饼油多糖多，这不符合健康标准，今年不让孩子买月饼，花那么多的钱还不如买几斤新鲜蔬菜、时令水果，保持清淡饮食，还有利于健康。”　</p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">　　月饼象征团圆，是中秋节必食之品。无中秋可以吃月饼，而无月饼则不成中秋。所以，尽管月饼是高糖高油，并不能列入健康食品中去，但中秋节，月饼还是要是吃。那么怎么吃，才能让这应景之食成为健康之品?　</p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<img src=\"/data/upload/6i/3l/523aa91d4dc98_600x480.jpg\" style=\"margin: 0px; padding: 0px; border: 0px none; display: inline;\" alt=\"\" /></p><p class=\"pictext\" align=\"center\" style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">　　<strong>首先，你可以吃月饼吗？</strong></p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">　　专家特别提醒说，下列人员不吃或少吃月饼：</p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">　　1． 糖尿病患者不可吃月饼：因月饼含糖量高，若患者吃了月饼后，可使血糖急剧升高，导致病情加重。</p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">　　2． 高血压、高血脂及冠心病等心脑血管疾病的患者千万不可吃月饼：因为月饼中的糖和油脂等可增加血液的粘稠度，加重心脏和大脑缺血程度，甚至可诱发心肌梗塞。</p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">　　3． 胃、十二指肠等消化系统疾病患者要控制食用月饼：因月饼使胃酸分泌大量增加，对疾病的愈合不利。</p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">　　4． 胆囊炎、胆石症或慢性胰腺炎的患者不能吃月饼：因吃月饼很容易使原有的慢性胆囊炎、慢性胰腺炎复发，引起剧烈疼痛，胆结石患者吃月饼后会刺激胆汁，引起疾病再度发作。</p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">　　5．老年人：因消化吸收功能较差，多吃会增加胃肠负担，引起消化不良、腹泻等。</p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">　　6． 婴幼儿：因消化系统发育不够健全，消化器官十分娇嫩和脆弱，难以承受高糖、高脂肪的食物。</p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">　　此外，患有慢性腹泻、急性肝炎和恢复期肝炎的患者，也不可食月饼；患龋齿的患者也要少吃月饼，而且吃后要立即刷牙洁齿。</p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\"></p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">&nbsp;<img src=\"/data/upload/6c/9r/523aa94233554_600x480.jpg\" alt=\"\" /></p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">&nbsp; &nbsp; &nbsp; &nbsp;少吃巧搭配才养生</p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">　　<strong>搭配一 月饼+葡萄酒</strong></p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">　　葡萄酒+月饼，古已有之，所谓“玉宇琼浆”就是说明月悬照之夜畅饮美酒了。现今的月饼，从口味到形状都越来越丰富多样，但月饼美酒的搭配原理却始终如一：月饼先咸后甜，酒先清淡圆润后浓郁悠香，酩酿回味悠悠长，二者相互交织，方能体味出醇香与迷人的韵味。</p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">&nbsp; &nbsp; &nbsp; &nbsp;<strong>搭配二 月饼+水果</strong></p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">　　饼搭配时令水果食用也是有讲究的，可多选择橙色、酸味、富含维生素Ｃ的品种，如奇异果、番石榴、橙子、西瓜、香蕉等。这些水果能对高脂肪、高蛋白物质的代谢起到抗氧化作用，从而保护心脑血管。</p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">&nbsp; &nbsp; &nbsp; <strong>&nbsp;搭配三 月饼＋茶</strong></p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">　　莲蓉月饼搭配大红袍茶</p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">　　搭配理由：大红袍茶香味醇厚，甘爽怡人。茶入口，香气馥郁，久久不散。与“甜美”的莲蓉月饼搭配，能将月饼的香浓更好地衬托出来。</p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">　　贴士：大红袍贵在稀缺，在没有人工种植之前，其来源于武夷山风景区九龙窠悬岩峭壁上的六棵茶树，所以非常珍贵，价格高昂。新中国成立后，武夷山市茶叶研究所经过不断研究，终于在上世纪80年代初使大红袍母树无性繁殖获得成功。经过20多年的推广，大红袍的种植面积在不断扩大，因此现在产量有所增加，价格也有所下降，普通市民才可享受到这带着神秘色彩的贡品。</p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">&nbsp; &nbsp;&nbsp;<img src=\"/data/upload/8a/4c/523aa9979c40c_600x480.jpg\" alt=\"\" /></p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">　　低糖月饼搭配碧螺春</p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">　　搭配理由：碧螺春口感清香、润滑，喝后喉咙特别舒服。与健康的低糖月饼相搭配，味道甘美而又不会“喧宾夺主”，恰到好处。</p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">　　贴士：泡茶需要好水，正所谓“精茗蕴香，借水而发，无水不可与论茶也。”</p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">　　据老茶客经验介绍，山泉水、溪水是泡茶的上品。山泉水大多出自于岩石重叠的山峦，山上植被繁茂。从山岩断层细流汇集而成的山泉，富含二氧化碳和各种对人体有益的微量元素，而经过砂石过滤的泉水，水质晶莹，含氯、铁等化合物极少。用这种泉水泡茶，能使茶的香味得到最大的发挥。</p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\"></p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">&nbsp; &nbsp; <strong>&nbsp; </strong>水果月饼搭配花茶</p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">　　搭配理由：水果月饼已具备水果的清香，配上香味清新的花茶，正好“碰撞”出美妙的香甜。</p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">　　贴士：花茶中推荐茉莉花茶。茉莉花茶是用特种工艺造型茶，或经过精制后的绿茶茶坯混合茉莉鲜花制作而成的茶叶品种。</p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">&nbsp;<img src=\"/data/upload/5y/1j/523aa96c154ec_600x480.jpg\" alt=\"\" /></p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">　　<strong>搭配四 月饼+酸梅汤</strong></p><p style=\"margin: 0px 0px 25px; padding: 0px; line-height: 25px; font-size: 14px; font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif;\">　　比起同样古色古香却口感苦涩的中国茶来，酸甜清淡的酸梅汤配月饼可谓是天作之合。酸梅汤营养丰富，属于纯天然的健康饮品，吃完月饼，再喝杯酸梅汤，不但可清爽味蕾，更能去除肠胃里的油腻。而且，酸梅汤是我国的传统饮料，和月饼这种传统节日食品相搭配，极好的烘托出了中秋节浓浓的传统情怀。不过，酸梅汤虽好，在选择的时候大家也得多注意，不要选择那些含添加剂的酸梅汤。</p><br /><br />', '', '', '1379576333', '257', '1', '', '', '', '3', '周边小编', '1'), ('23', '今道北海城市的美食肴', '', '<span style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, clean, sans-serif; font-size: 14px; line-height: 22px;\">首先，来北海这个著名的海滨城市当然是少不了生猛海鲜了，为什么这么讲，下面周边网为您介绍北海的海鲜美食。特别是依滩傍海的海鲜大排档，各类鱼虾贝蟹置池中饲养，任客挑选。海边就餐，把酒临风，涛声助兴，吃海鲜、观海面、沐海风，个中情趣，妙不可言。但是有很多海鲜店宰客也是蛮严重的。所以推荐几处吃海鲜的地方：如果上档次吃海鲜的地方，那就选择在银滩边或外沙海鲜岛去吃，价格是贵了些。如果想吃的实惠，可以去贵州路半坡，不过那里的海鲜都是冰冻的，还有就是可以去外沙桥附近的大排档，价格也不贵的。</span><br style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, clean, sans-serif; font-size: 14px; line-height: 22px;\" /><br style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, clean, sans-serif; font-size: 14px; line-height: 22px;\" /><p><span style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, clean, sans-serif; font-size: 14px; line-height: 22px;\">北海当地比较著名的特色菜有：鸡饭（精选本地土鸡，皮黄肉白骨红，味香口滑），老虎鱼汤、椒盐弹虾、清蒸笠鱼、白灼沙虫、姜葱花蟹、白灼中虾、清蒸插螺、梅子蒸普鱼等等。其中来北海必吃的就是白灼沙虫，沙虫形似肠子，又名沙肠虫，生活在海边的沙地里，味道鲜美脆嫩，胜过海参、鱼翅等海鲜，是北海一带的特产。就看你能不能克服心理障碍，吃不吃的下这道美味了！</span></p><p><span style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, clean, sans-serif; font-size: 14px; line-height: 22px;\"><img src=\"/data/upload/9g/6c/523bacdb4b6d1_600x480.jpg\" alt=\"\" /><br /></span></p><br style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, clean, sans-serif; font-size: 14px; line-height: 22px;\" /><span style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, clean, sans-serif; font-size: 14px; line-height: 22px;\">说道北海小吃，最有名的要数牛腩粉、猪脚粉了。北海的早餐店里，一碗牛腩粉或猪脚粉，再加上一杯清茶，是北海人最称道的美味早餐。至于吃的地方，北海的大小街巷都有的，不过最出名、最地道的还是三弟四妹的店。另外还有煎堆、风吹饼、米 米乙（读“ye”,不过打不出来）等小吃也是很有特色的，不过得深入各个市场才能接触的到。</span><br style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, clean, sans-serif; font-size: 14px; line-height: 22px;\" /><br style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, clean, sans-serif; font-size: 14px; line-height: 22px;\" /><span style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, clean, sans-serif; font-size: 14px; line-height: 22px;\">对于吃海鲜也要注意几个问题：</span><br style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, clean, sans-serif; font-size: 14px; line-height: 22px;\" /><span style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, clean, sans-serif; font-size: 14px; line-height: 22px;\">1、吃海鲜不宜喝啤酒，对肾和关节有损伤。</span><br style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, clean, sans-serif; font-size: 14px; line-height: 22px;\" /><span style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, clean, sans-serif; font-size: 14px; line-height: 22px;\">2、海鲜不宜和柿子、葡萄、石榴、青苹果等水果同食，易刺激肠道引起呕吐。</span><br style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, clean, sans-serif; font-size: 14px; line-height: 22px;\" /><span style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, clean, sans-serif; font-size: 14px; line-height: 22px;\">3、许多北方人或许不适应生猛海鲜的刺激，最好准备一些消化类的药物。</span><br style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, clean, sans-serif; font-size: 14px; line-height: 22px;\" /><span style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, clean, sans-serif; font-size: 14px; line-height: 22px;\">4、吃完海鲜，可以喝一杯当地的清茶，去火清肠胃，舒爽无比。</span><br style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, clean, sans-serif; font-size: 14px; line-height: 22px;\" /><span style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, clean, sans-serif; font-size: 14px; line-height: 22px;\">5、外沙岛前两年很受欢迎，现在商业化越来越浓，游客更喜欢在涠洲岛品尝生猛海鲜，既新鲜又实惠。</span><br style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, clean, sans-serif; font-size: 14px; line-height: 22px;\" /><br style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, clean, sans-serif; font-size: 14px; line-height: 22px;\" /><p><span style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, clean, sans-serif; font-size: 14px; line-height: 22px;\">夜晚逛北海夜市也是吃货们的最爱。北海夜市集中在长青路烧烤一条街上，一到了夜晚这边便是热闹沸腾的，一眼望去全是海鲜烧烤摊，当然也有一些冰粥凉饮、酸甜角小吃啦，还有海鲜的最佳搭档—凉茶摊（美食大三通可是有几集都是讲北海的哦，可以搜搜看看）。</span></p><p><span style=\"font-family:arial, helvetica, clean, sans-serif;color:#333333;\"><span style=\"font-size: 14px; line-height: 22px;\"><br /></span></span></p><p><span style=\"font-family:arial, helvetica, clean, sans-serif;color:#333333;\"><span style=\"font-size: 14px; line-height: 22px;\">每一位广西人民都是美食的爱好者，在这里，祝福所有的朋友们，未来的每一天美食永远陪在自己的身边。</span></span></p>', '', '', '1379642770', '335', '1', '', '', '', '3', '周边小编', '1'), ('24', '怎样解决秋季眼的干涩', '', '<p style=\"BORDER-BOTTOM: 0px; POSITION: relative; BORDER-LEFT: 0px; PADDING-BOTTOM: 10px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 20px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 20px; FONT: 14px/24px Arial, Helvetica, sans-serif; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(102,102,102); VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 10px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">秋天到了，气候干燥，我们往往感觉到眼睛干涩，一般来说，这是因为干燥的环境里，水分蒸发得比较快，人体内的水分也同样容易消耗。所以，人们在秋天容易感觉到嘴巴干、眼睛涩痒。</p><p style=\"BORDER-BOTTOM: 0px; POSITION: relative; TEXT-ALIGN: center; BORDER-LEFT: 0px; PADDING-BOTTOM: 10px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 20px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 20px; FONT: 14px/24px Arial, Helvetica, sans-serif; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(102,102,102); VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 10px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><img style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px auto; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; DISPLAY: block; MAX-WIDTH: 550px; FONT-SIZE: 14px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\" class=\"conimg\" alt=\"眼睛500a.jpg\" src=\"http://images.meishij.net/p/20130916/fa83faf2da71a33d160c622879866216.jpg\" width=\"500\" height=\"429\" /></p><p style=\"BORDER-BOTTOM: 0px; POSITION: relative; BORDER-LEFT: 0px; PADDING-BOTTOM: 10px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 20px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 20px; FONT: 14px/24px Arial, Helvetica, sans-serif; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(102,102,102); VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 10px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">&nbsp;　　一、电脑工作者</p><p style=\"BORDER-BOTTOM: 0px; POSITION: relative; BORDER-LEFT: 0px; PADDING-BOTTOM: 10px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 20px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 20px; FONT: 14px/24px Arial, Helvetica, sans-serif; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(102,102,102); VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 10px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">　　秋高气爽的季节，一些每天工作都对着电脑的白领们开始觉得眼干、眼发热、看东西不清楚，还有眼疾发痒，不时的想揉眼睛等现象。专家认为，在空气干燥、不流通的环境中经常盯着电脑看，致使眨眼次数减少，以至于泪液的蒸发加速而成为干眼症的诱因之一。</p><p style=\"BORDER-BOTTOM: 0px; POSITION: relative; BORDER-LEFT: 0px; PADDING-BOTTOM: 10px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 20px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 20px; FONT: 14px/24px Arial, Helvetica, sans-serif; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(102,102,102); VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 10px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">　　缓解秋季眼干、眼涩，应注意办公、生活环境的温度、湿度，可在屋中摆放绿色植物、水养植物或放一盆水，都可以使房间内空气湿润。</p><p style=\"BORDER-BOTTOM: 0px; POSITION: relative; BORDER-LEFT: 0px; PADDING-BOTTOM: 10px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 20px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 20px; FONT: 14px/24px Arial, Helvetica, sans-serif; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(102,102,102); VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 10px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">　　滋阴补眼食材推荐：日常注意多摄入些含维生素A的食物。水果雪梨、葡萄等都有滋阴功效，此外，将麦冬、决明子、菊花等泡茶喝也可起到滋阴明目的作用。</p><table style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px auto; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; BORDER-SPACING: 0px; PADDING-RIGHT: 0px; BORDER-COLLAPSE: collapse; FONT: 14px/24px Arial, Helvetica, sans-serif; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(102,102,102); VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"500\" align=\"center\"><tbody style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 14px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\"><tr style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 14px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\"><td style=\"BORDER-BOTTOM: 0px; TEXT-ALIGN: center; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\"><img style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\" class=\"conimg\" alt=\"知悉你的明眸，告别眼睛干痒Cy.jpg\" src=\"http://images.meishij.net/p/20130916/843afd96da94163bfabecae610cb7c3e.jpg\" /></td><td style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\"><img style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\" class=\"conimg\" alt=\"知悉你的明眸，告别眼睛干痒Cr.jpg\" src=\"http://images.meishij.net/p/20130916/8129e46c75972dd0b50a266671dfc893.jpg\" /></td><td style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\"><img style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\" class=\"conimg\" alt=\"知悉你的明眸，告别眼睛干痒Qm.jpg\" src=\"http://images.meishij.net/p/20130916/db35cfd79a83ee066bb3a07794d4cff1.jpg\" /></td></tr><tr style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 14px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\"><td style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\"><div style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\" align=\"center\">双椒炒猪肝</div></td><td style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\"><div style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\" align=\"center\">青蒜胡萝卜丝</div></td><td style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\"><div style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\" align=\"center\">凉拌紫甘蓝</div></td></tr></tbody></table><p style=\"BORDER-BOTTOM: 0px; POSITION: relative; BORDER-LEFT: 0px; PADDING-BOTTOM: 10px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 20px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 20px; FONT: 14px/24px Arial, Helvetica, sans-serif; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(102,102,102); VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 10px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">　　二、季节导致眼干</p><p style=\"BORDER-BOTTOM: 0px; POSITION: relative; BORDER-LEFT: 0px; PADDING-BOTTOM: 10px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 20px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 20px; FONT: 14px/24px Arial, Helvetica, sans-serif; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(102,102,102); VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 10px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">　　到了秋天，因为气候干燥，我们往往感觉到眼睛干涩，一般来说，这是因为干燥的环境里，水分蒸发得比较快，人体内的水分也同样容易消耗。所以，人们在秋天容易感觉到嘴巴干、嘴唇紧绷、眼睛干涩。</p><p style=\"BORDER-BOTTOM: 0px; POSITION: relative; BORDER-LEFT: 0px; PADDING-BOTTOM: 10px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 20px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 20px; FONT: 14px/24px Arial, Helvetica, sans-serif; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(102,102,102); VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 10px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">　　秋季养眼护眼食材推荐：冬瓜、黄瓜、橘子、梨、苹果、西瓜、菊花、枸杞子、决明子、山楂、猪肝、山药、红枣、绿豆</p><table style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px auto; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; BORDER-SPACING: 0px; PADDING-RIGHT: 0px; BORDER-COLLAPSE: collapse; FONT: 14px/24px Arial, Helvetica, sans-serif; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(102,102,102); VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"500\" align=\"center\"><tbody style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 14px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\"><tr style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 14px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\"><td style=\"BORDER-BOTTOM: 0px; TEXT-ALIGN: center; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\"><img style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\" class=\"conimg\" alt=\"知悉你的明眸，告别眼睛干痒Cy.jpg\" src=\"http://images.meishij.net/p/20130916/eccb59bd72cf233460f56f0596ec954f.jpg\" /></td><td style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\"><img style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\" class=\"conimg\" alt=\"知悉你的明眸，告别眼睛干痒Cr.jpg\" src=\"/data/upload/0l/2z/523f0c98b16c1_600x480.jpg\" /></td><td style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\"><img style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\" class=\"conimg\" alt=\"知悉你的明眸，告别眼睛干痒Qm.jpg\" src=\"http://images.meishij.net/p/20130916/ca83df847a4c3ec598f428ce4a573a48.jpg\" /></td></tr><tr style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 14px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\"><td style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\"><div style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\" align=\"center\">枸杞木耳炒山药</div></td><td style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\"><div style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\" align=\"center\">蓑衣黄瓜</div></td><td style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\"><div style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\" align=\"center\">肉末蒸冬瓜</div></td></tr></tbody></table><p style=\"BORDER-BOTTOM: 0px; POSITION: relative; BORDER-LEFT: 0px; PADDING-BOTTOM: 10px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 20px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 20px; FONT: 14px/24px Arial, Helvetica, sans-serif; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(102,102,102); VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 10px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">　　三、熬夜上火导致眼干</p><p style=\"BORDER-BOTTOM: 0px; POSITION: relative; BORDER-LEFT: 0px; PADDING-BOTTOM: 10px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 20px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 20px; FONT: 14px/24px Arial, Helvetica, sans-serif; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(102,102,102); VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 10px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">　　经常熬夜的人既导致睡眠时间不足，眼睛得不到很好的休息，而且还会影响肝脏夜间的自我修复，以至肝火旺盛。《黄帝内经》里就曾说过：“肝开窍于目”，更说明眼与肝的关系十分密切。所以充足的休息和饮水对纾缓眼睛干涩也有一定的作用。</p><p style=\"BORDER-BOTTOM: 0px; POSITION: relative; BORDER-LEFT: 0px; PADDING-BOTTOM: 10px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 20px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 20px; FONT: 14px/24px Arial, Helvetica, sans-serif; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(102,102,102); VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 10px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">　　如感觉视疲劳加晨起眼睛干，可在临睡前用毛巾热敷眼部。用不超过50℃的毛巾敷眼，可有助于眼部血液扩张、促进血液循环，从而消除眼部浮肿、充血等现象，也能将泪水排放孔张开，让泪液分泌足够，缓解眼睛干涩、刺痛现象。</p><table style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px auto; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; BORDER-SPACING: 0px; PADDING-RIGHT: 0px; BORDER-COLLAPSE: collapse; FONT: 14px/24px Arial, Helvetica, sans-serif; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(102,102,102); VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"500\" align=\"center\"><tbody style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 14px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\"><tr style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 14px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\"><td style=\"BORDER-BOTTOM: 0px; TEXT-ALIGN: center; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\"><img style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\" class=\"conimg\" alt=\"知悉你的明眸，告别眼睛干痒Cy.jpg\" src=\"http://images.meishij.net/p/20130916/1ef07e2068a068fad3c8b7408b3580eb.jpg\" /></td><td style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\"><img style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\" class=\"conimg\" alt=\"知悉你的明眸，告别眼睛干痒Cr.jpg\" src=\"http://images.meishij.net/p/20130916/b91867b295e8aeaf36838e523766a5a4.jpg\" /></td><td style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\"><img style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\" class=\"conimg\" alt=\"知悉你的明眸，告别眼睛干痒Qm.jpg\" src=\"http://images.meishij.net/p/20130916/26246fbaa301f1955fbc5e7b4746d596.jpg\" /></td></tr><tr style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 14px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\"><td style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\"><div style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\" align=\"center\">芹菜炒虾仁</div></td><td style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\"><div style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\" align=\"center\">苦瓜的甜蜜</div></td><td style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\"><div style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT-SIZE: 12px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\" align=\"center\">蒸苹果</div></td></tr></tbody></table><p style=\"BORDER-BOTTOM: 0px; POSITION: relative; BORDER-LEFT: 0px; PADDING-BOTTOM: 10px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 20px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 20px; FONT: 14px/24px Arial, Helvetica, sans-serif; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(102,102,102); VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 10px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">四、明目先养肝</p><p style=\"BORDER-BOTTOM: 0px; POSITION: relative; BORDER-LEFT: 0px; PADDING-BOTTOM: 10px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 20px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 20px; FONT: 14px/24px Arial, Helvetica, sans-serif; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(102,102,102); VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 10px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">　　中医里有一句话就是说肝和眼睛的关系，叫“肝开窍于目”。肝提供的血液和阴津就是滋养眼睛的，可以说，肝是明目的源泉。如果肝不好自然使眼睛得不到滋养，感觉到干涩。</p><p style=\"BORDER-BOTTOM: 0px; POSITION: relative; TEXT-ALIGN: center; BORDER-LEFT: 0px; PADDING-BOTTOM: 10px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 20px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 20px; FONT: 14px/24px Arial, Helvetica, sans-serif; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(102,102,102); VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 10px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><img style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px auto; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; DISPLAY: block; MAX-WIDTH: 550px; FONT-SIZE: 14px; VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\" class=\"conimg\" alt=\"养肝明目500a.jpg\" src=\"http://images.meishij.net/p/20130916/2bb1d39d435e0b21e0d63dfbb2558e98.jpg\" width=\"500\" height=\"429\" /></p><p style=\"BORDER-BOTTOM: 0px; POSITION: relative; BORDER-LEFT: 0px; PADDING-BOTTOM: 10px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 20px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 20px; FONT: 14px/24px Arial, Helvetica, sans-serif; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(102,102,102); VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 10px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">　　中医五行中认为金克木，在脏腑中则为肺金克肝木。秋季肺金亢盛，使肝木受到抑制，而肝木的阴津是濡养眼睛的源泉，当其受到抑制时，阴津分泌减少，使眼睛得不到濡养就会感觉干涩，原来就比较干涩的眼睛当然就会更加严重，所以说明目先要养肝。</p><p style=\"BORDER-BOTTOM: 0px; POSITION: relative; BORDER-LEFT: 0px; PADDING-BOTTOM: 10px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 20px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 20px; FONT: 14px/24px Arial, Helvetica, sans-serif; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(102,102,102); VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 10px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">　　养肝食材推荐：黑木耳、蘑菇、韭菜、西红柿、猕猴桃、苹果、猪血、牛肉、鱼类、黑豆、糯米、粗杂粮等食物。</p><table style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px auto; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; BORDER-SPACING: 0px; PADDING-RIGHT: 0px; BORDER-COLLAPSE: collapse; FONT: 14px/24px Arial, Helvetica, sans-serif; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(102,102,102); VERTICAL-ALIGN: baseline; BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"500\" align=\"center\"><tbody style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: 0px; PADDING-RIGHT: 0px; FONT